return items.map(item => {
  const json = item.json;
  // 1) Original caption or title
  const caption = json.caption || json.title || '';
  json.caption = caption;
  // 2) Count hashtags & strip them + strip URLs
  const hashtags = caption.match(/#\S+/g) || [];
  json.hashtagCount = hashtags.length;
  json.hasHashtags = json.hashtagCount > 0;
  const noUrls = caption.replace(/https?:\/\/\S+/gi, '');
  const textNoTags = noUrls.replace(/#[^\s#]+/g, '').replace(/\.{2,}/g, ' ').replace(/\s+/g, ' ').trim();
  json.textNoTags = textNoTags;
  json.hasOnlyHashtags = json.hasHashtags && textNoTags === '';
  // 3) Word array (no numbers-only, min length 2)
  let words = textNoTags.toLowerCase().split(/\W+/).filter(w => w.length > 1 && !/^\d+$/.test(w));
  const totalWords = words.length;
  json.totalWords = totalWords;
  // 4) Keyword hit lists
  const enKw = ['the','and','to','of','in','for','on','with','is','are','this','that','it','be','at','by','from','as','was','were','have','has','you','we','i'];
  const frKw = ['le','la','les','des','un','une','du','de','et','en','dans','pour','avec','est','sur','au','aux','ce','cet','cette','√ßa','pas','plus','que','qui','o√π','je','tu','il','elle','nous','vous','ils','elles'];
  const esKw = ['el','la','los','las','de','del','y','en','por','para','con','es','un','una','que','como','muy','pero','si','porque'];
  json.enHits = words.filter(w => enKw.includes(w)).length;
  json.frHits = words.filter(w => frKw.includes(w)).length;
  json.esHits = words.filter(w => esKw.includes(w)).length;
  // 5) Ratios
  json.enRatio = totalWords ? json.enHits / totalWords : 0;
  json.frRatio = totalWords ? json.frHits / totalWords : 0;
  json.esRatio = totalWords ? json.esHits / totalWords : 0;
  // 6) ASCII vs non-ASCII
  const asciiCount = textNoTags.replace(/[^\x00-\x7F]/g, '').length;
  json.asciiRatio = textNoTags.length ? asciiCount / textNoTags.length : 0;
  // 7) Accent & script flags
  json.hasFrenchAccent = /[√©√®√™√´√†√¢√Æ√Ø√¥√∂√π√ª√ß≈ì√¶]/i.test(textNoTags);
  json.hasSpanishAccentStrict = /[√±¬°¬ø]/.test(textNoTags);
  json.hasArabicOrCJK = /[\u0600-\u06FF\u0750-\u077F\u4E00-\u9FFF\u3040-\u30FF\uAC00-\uD7AF]/u.test(textNoTags);
  return { json };
});
