// Lead deduplication and categorization logic
const existingLeads = $('Get Existing Leads').all().map(i => i.json);
const newLeads = $('Clean Data Format').all().map(i => i.json);

return newLeads.map(lead => {
  const match = existingLeads.find(existing =>
    existing.company_name?.toLowerCase().trim() === lead.company_name?.toLowerCase().trim()
  );

  const existingSummary = match?.job_summary ?? '';
  const existingJobPoster = match?.jobposter_profile ?? '';

  const isSummaryMissing = existingSummary.trim() === '';
  const isJobPosterMissing = existingJobPoster.trim() === '';

  let category;
  let processingReason = '';

  if (!match || isSummaryMissing) {
    category = 1;
    processingReason = !match ? 'New company lead' : 'Existing lead missing AI analysis';
  } else if (isJobPosterMissing) {
    category = 2;
    processingReason = 'Lead ready for enrichment';
  } else {
    category = 3;
    processingReason = 'Complete lead - already processed';
  }

  return {
    ...lead,
    category,
    processingReason,
    isExistingLead: !!match
  };
});
