const autocompleteItems = $items("Autocomplete");
const paaItems = $items("Format PAA (SerpAPI)");
const topicItems = $items("Broad Words");

const output = [];

function normalize(str) {
  return (str || "").toLowerCase().trim();
}

for (let i = 0; i < topicItems.length; i++) {
  const topicRaw = topicItems[i].json["Blog Inspiration"] || "Unknown Topic";
  const topic = topicRaw.trim();

  // Normalize for matching
  const normTopic = normalize(topic);

  // Get autocomplete suggestions
  const autocomplete = autocompleteItems[i]?.json?.autocomplete || [];

  // Get PAA from autocomplete
  const paaFromAutocomplete = autocompleteItems[i]?.json?.paa || [];

  // Match SerpAPI result by normalized topic
  const matchingPAAObj = paaItems.find(p => normalize(p.json.topic) === normTopic);
  const paaFromSerpAPI = matchingPAAObj?.json?.paa || [];

  // Merge + clean
  const allPAA = [...paaFromAutocomplete, ...paaFromSerpAPI].filter(
    q => q && typeof q === "string" && !q.toLowerCase().includes("no paa")
  );

  output.push({
    json: {
      topic,
      autocomplete,
      paa: allPAA.length ? allPAA : undefined
    }
  });
}

return output;

