  const inspiration = item.json["Blog Inspiration"];
  // Simplify to just the last 3 words or a fallback keyword
  const words = inspiration.split(" ");
  const shortTopic = words.slice(-3).join(" ");
  item.json.topic = shortTopic || "photo ideas";
  return item;
});

const formatted = [];

for (const item of items) {
  const data = item.json;
  const query = data.search_information?.query_displayed || "Unknown query";

  const paaList = [];

  // ‚úÖ PRIORITY 1: related_questions (People Also Ask)
  if (Array.isArray(data.related_questions)) {
    for (const q of data.related_questions) {
      if (q.question && q.snippet) {
        paaList.push(`(PAA) ${q.question} ‚Äî ${q.snippet}`);
      } else if (q.question) {
        paaList.push(`(PAA) ${q.question}`);
      }
    }
  }

  // üîÑ FALLBACK 1: organic_results
  if (paaList.length === 0 && Array.isArray(data.organic_results)) {
    for (const result of data.organic_results) {
      if (result.title && result.snippet) {
        paaList.push(`(Organic) ${result.title} ‚Äî ${result.snippet}`);
      }
    }
  }

  // üîÑ FALLBACK 2: answer_box
  if (paaList.length === 0 && data.answer_box?.title && data.answer_box?.snippet) {
    paaList.push(`(Answer Box) ${data.answer_box.title} ‚Äî ${data.answer_box.snippet}`);
  }

  // üîÑ FALLBACK 3: related_searches
  if (paaList.length === 0 && Array.isArray(data.related_searches)) {
    for (const rel of data.related_searches) {
      if (rel.query) {
        paaList.push(`(Related Search) ${rel.query}`);
      }
    }
  }

  formatted.push({
    json: {
      topic: query,
      paa: paaList.length > 0 ? paaList : null
    }
  });
}

return formatted;
