const items = await $input.all();
const certificates = [];

for (const item of items) {
  const certs = Array.isArray(item.json) ? item.json : [item.json];

  for (const cert of certs) {
    certificates.push({
      CertificateArn: cert.CertificateArn,
      DomainName: cert.DomainName,
      Status: cert.Status,
      InUse: cert.InUse ? 'Yes' : 'No',
      CreatedAt: new Date(cert.CreatedAt * 1000).toISOString(),
      IssuedAt: new Date(cert.IssuedAt * 1000).toISOString(),
      NotBefore: new Date(cert.NotBefore * 1000).toISOString(),
      NotAfter: new Date(cert.NotAfter * 1000).toISOString(),
      RenewalEligibility: cert.RenewalEligibility,
      SubjectAlternativeNames: (cert.SubjectAlternativeNameSummaries || []).join(', '),
      KeyAlgorithm: cert.KeyAlgorithm,
      KeyUsages: (cert.KeyUsages || []).join(', '),
      ExtendedKeyUsages: (cert.ExtendedKeyUsages || []).join(', '),
      Type: cert.Type,
      Exported: cert.Exported ? 'Yes' : 'No',
    });
  }
}

return [
  {
    json: {
      certificates,
      total: certificates.length,
      expiredCount: certificates.filter(c => c.Status === 'EXPIRED').length,
      inUseCount: certificates.filter(c => c.InUse === 'Yes').length,
    }
  }
];
