const allWorkflows = $('Aggregate').last().json.data;
let html = '';

const documentedWebhookWorkflows = allWorkflows.filter(item => {
  if (!item.nodes || item.nodes.length === 0) {
    return false;
  }
  const hasWebhook = item.nodes.some(node => node.type === 'n8n-nodes-base.webhook');
  const hasDocsNote = item.nodes.some(node => node.type === 'n8n-nodes-base.set' && node.name === 'API_DOCS');
  return hasWebhook && hasDocsNote;
});


if (documentedWebhookWorkflows.length > 0) {
  html += '<div class="accordion" id="docsAccordion">';

  const n8nBaseUrl = $("Execute").last().json.url_base || 'https://n8n.io/';

  for (const workflow of documentedWebhookWorkflows) {
    const docsNode = workflow.nodes.find(n => n.name === 'API_DOCS');

    if (docsNode) {
      const jsonString = docsNode.parameters.jsonOutput || JSON.stringify(docsNode.parameters.docsData);
      let docsData = {};
      let webhookNode = null;

      try {
        docsData = JSON.parse(jsonString);
        webhookNode = workflow.nodes.find(n => { return n.type === 'n8n-nodes-base.webhook' && n.name === docsData.webhook });
      } catch (e) {
        console.error(`Erro ao parsear JSON no workflow: ${workflow.name}`, e);
        continue;
      }

      if(webhookNode){
        const method = (docsData.method || "POST").toUpperCase();
        const uniqueId = webhookNode.parameters.path.replace(/[^a-zA-Z0-9]/g, '');

        // --- make CURL
        const fullUrl = `${n8nBaseUrl}webhook/${webhookNode.parameters.path}`;

        let curlCommand = `curl -X ${method}\\`;
        if (method === 'POST' || method === 'PUT' || method === 'PATCH') {
          const requestBodyString = JSON.stringify(docsData.requestBody || {});
          curlCommand += `
          -H 'Content-Type: application/json' \\
          -d '${requestBodyString}' \\`;
                }
                curlCommand += `
          ${fullUrl}`;

        html += `
          <div class="accordion-item">
            <h2 class="accordion-header" id="heading-${uniqueId}">
              <button class="accordion-button collapsed" type="button" data-bs-toggle="collapse" data-bs-target="#collapse-${uniqueId}" aria-expanded="false" aria-controls="collapse-${uniqueId}">
                <span class="method method-${method.toLowerCase()}">${method}</span>
                <span class="endpoint-path">/webhook/${webhookNode.parameters.path}</span>
              </button>
            </h2>
            <div id="collapse-${uniqueId}" class="accordion-collapse collapse" aria-labelledby="heading-${uniqueId}" data-bs-parent="#docsAccordion">
              <div class="accordion-body">
                <p><strong>${docsData.summary || 'No summary'}</strong></p>
                <p>${docsData.description || 'No description.'}</p>
                <hr style="border-color: var(--bs-border-color);">

                <h6>cURL Command:</h6>
                <pre><code>${curlCommand}</code></pre>

                <h6>Request Body:</h6>
                <pre><code>${JSON.stringify(docsData.requestBody || {}, null, 2)}</code></pre>
                <h6>Success Response (${docsData.successCode || 'N/A'}):</h6>
                <pre><code>${JSON.stringify(docsData.successResponse || {}, null, 2)}</code></pre>
                <h6>Error Response (${docsData.errorCode || 'N/A'}):</h6>
                <pre><code>${JSON.stringify(docsData.errorResponse || {}, null, 2)}</code></pre>
              </div>
            </div>
          </div>
        `;
      }
    }
  }
  html += '</div>';
} else {
  html += '<div class="alert alert-info">Nenhum endpoint documentado foi encontrado.</div>';
}

return [{
  json: {
    htmlContent: html
  }
}];

