parameters.inputSchema:
{
    "type": "object",
    "properties": {
        "characters": {
            "type": "array",
            "description": "the list of characters in the scene",
            "items": {
                "type": "object",
                "properties": {
                    "name": {
                        "type": "string",
                        "description": "the name of the character"
                    },
                    "description": {
                        "type": "string",
                        "description": "the detailed description of the character (visual outlook)"
                    }
                },
                "required": [
                    "name",
                    "description"
                ]
            }
        },
        "scene_description": {
            "type": "string",
            "description": "the detailed description of the scene"
        },
        "camera_movement": {
            "type": "string",
            "description": "the description of the camera movement (if any)"
        },
        "object_movements": {
            "type": "string",
            "description": "the detailed description of the movement of the objects on the screen"
        },
        "sound_effects": {
            "type": "string",
            "description": "the sound effects the viewer can hear during the scene"
        }
    },
    "required": [
        "characters",
        "scene_description",
        "camera_movement",
        "object_movements",
        "sound_effects"
    ]
}
