// Define Google Docs to monitor
const docsToMonitor = [
  {
    id: 'DOC_ID_1_HERE',
    name: 'Project Status Doc',
    category: 'Projects'
  },
  {
    id: 'DOC_ID_2_HERE', 
    name: 'Meeting Notes',
    category: 'Meetings'
  },
  {
    id: 'DOC_ID_3_HERE',
    name: 'Team Updates',
    category: 'Updates'
  }
  // Add more docs as needed
];

// Calculate date range for last week
const now = new Date();
const lastWeek = new Date(now.getTime() - (7 * 24 * 60 * 60 * 1000));

console.log(`Checking docs for updates between ${lastWeek.toISOString()} and ${now.toISOString()}`);

const docsList = {
  docs: docsToMonitor,
  check_date_start: lastWeek.toISOString(),
  check_date_end: now.toISOString(),
  total_docs: docsToMonitor.length
};

return docsToMonitor.map(doc => ({ json: doc }));

// Combine doc content with metadata and check if updated
const docContent = $('Get Doc Content').item.json;
const docMetadata = $('Get Doc Metadata').item.json;
const docInfo = $input.first().json;

// Check if doc was modified in the last week
const lastModified = new Date(docMetadata.modifiedTime);
const oneWeekAgo = new Date(Date.now() - (7 * 24 * 60 * 60 * 1000));
const wasUpdated = lastModified > oneWeekAgo;

if (!wasUpdated) {
  console.log(`Document ${docInfo.name} not updated in last week, skipping`);
  return null;
}

// Extract text content from Google Docs structure
let textContent = '';
if (docContent.body && docContent.body.content) {
  docContent.body.content.forEach(element => {
    if (element.paragraph && element.paragraph.elements) {
      element.paragraph.elements.forEach(textElement => {
        if (textElement.textRun && textElement.textRun.content) {
          textContent += textElement.textRun.content;
        }
      });
    }
  });
}

// Clean up text content
textContent = textContent.replace(/\s+/g, ' ').trim();

const processedDoc = {
  id: docInfo.id,
  name: docInfo.name,
  category: docInfo.category,
  content: textContent,
  last_modified: docMetadata.modifiedTime,
  modified_by: docMetadata.lastModifyingUser?.displayName || 'Unknown',
  version: docMetadata.version,
  word_count: textContent.split(' ').length,
  was_updated: wasUpdated,
  content_preview: textContent.substring(0, 200) + '...'
};

console.log(`Processed doc: ${processedDoc.name} (${processedDoc.word_count} words, updated: ${processedDoc.was_updated})`);

return {
  json: processedDoc
};

// Aggregate all processed docs for AI summarization
const allDocs = $input.all();
const updatedDocs = allDocs.filter(item => item.json.was_updated);

if (updatedDocs.length === 0) {
  console.log('No documents were updated this week');
  return {
    json: {
      has_updates: false,
      message: 'No documents were updated this week.',
      summary: 'No weekly summary needed - all documents remain unchanged.'
    }
  };
}

// Prepare content for AI summarization
let combinedContent = 'WEEKLY DOCUMENT UPDATES SUMMARY REQUEST:\n\n';

updatedDocs.forEach((doc, index) => {
  combinedContent += `DOCUMENT ${index + 1}: ${doc.json.name} (${doc.json.category})\n`;
  combinedContent += `Last Modified: ${new Date(doc.json.last_modified).toLocaleDateString()} by ${doc.json.modified_by}\n`;
  combinedContent += `Word Count: ${doc.json.word_count}\n`;
  combinedContent += `Content: ${doc.json.content}\n\n---\n\n`;
});

const aggregatedData = {
  has_updates: true,
  total_updated_docs: updatedDocs.length,
  docs_summary: updatedDocs.map(doc => ({
    name: doc.json.name,
    category: doc.json.category,
    modified_by: doc.json.modified_by,
    last_modified: doc.json.last_modified
  })),
  combined_content: combinedContent,
  week_range: `${new Date(Date.now() - (7 * 24 * 60 * 60 * 1000)).toLocaleDateString()} - ${new Date().toLocaleDateString()}`
};

console.log(`Aggregated ${aggregatedData.total_updated_docs} updated documents for AI summary`);

return {
  json: aggregatedData
};

// Prepare final email content
const summaryData = $('Aggregate Updated Docs').item.json;
const aiResponse = $input.first().json;

if (!summaryData.has_updates) {
  return {
    json: {
      send_email: false,
      subject: 'Weekly Document Updates - No Changes',
      body: summaryData.message
    }
  };
}

const aiSummary = aiResponse.choices[0].message.content;

// Create professional email
const emailContent = {
  send_email: true,
  subject: `üìÑ Weekly Document Updates Summary - ${summaryData.week_range}`,
  body: `Dear Team,\n\nHere's your weekly document updates summary for ${summaryData.week_range}:\n\n${aiSummary}\n\nüìä UPDATED DOCUMENTS (${summaryData.total_updated_docs}):\n${summaryData.docs_summary.map(doc => `‚Ä¢ ${doc.name} (${doc.category}) - Updated by ${doc.modified_by} on ${new Date(doc.last_modified).toLocaleDateString()}`).join('\n')}\n\n---\nThis summary was automatically generated by your n8n workflow.\nTo modify monitored documents, update the workflow configuration.\n\nBest regards,\nYour Automation Assistant`,
  html_body: `
    <div style="font-family: Arial, sans-serif; max-width: 600px; margin: 0 auto;">
      <h2>üìÑ Weekly Document Updates Summary</h2>
      <p><strong>Period:</strong> ${summaryData.week_range}</p>

      <div style="background: #f5f5f5; padding: 20px; border-radius: 8px; margin: 20px 0;">
        ${aiSummary.replace(/\n/g, '<br>')}
      </div>

      <h3>üìä Updated Documents (${summaryData.total_updated_docs})</h3>
      <ul>
        ${summaryData.docs_summary.map(doc => `<li><strong>${doc.name}</strong> (${doc.category})<br><small>Updated by ${doc.modified_by} on ${new Date(doc.last_modified).toLocaleDateString()}</small></li>`).join('')}
      </ul>

      <hr>
      <p style="color: #666; font-size: 12px;">This summary was automatically generated by your n8n workflow.</p>
    </div>
  `
};

console.log('Prepared email summary:', {
  subject: emailContent.subject,
  docs_count: summaryData.total_updated_docs
});

return {
  json: emailContent
};
