const invoices = $input.all();
const taxSummary = new Map();

console.log('Processing invoices for tax summary:', invoices.length);

for (const invoice of invoices) {
  // Handle Stripe API response structure
  const invoiceData = invoice.json.data || invoice.json;

  if (!invoiceData || !Array.isArray(invoiceData)) {
    console.log('Skipping invalid invoice data');
    continue;
  }

  for (const invoiceItem of invoiceData) {
    const invoiceDate = new Date(invoiceItem.created * 1000);
    const period = `${invoiceDate.getFullYear()}-${String(invoiceDate.getMonth() + 1).padStart(2, '0')}`;

    // Extract country from account or customer data
    const country = invoiceItem.account_country || 
                   invoiceItem.customer_details?.address?.country || 
                   'Unknown';

    // Process line items with tax information
    if (invoiceItem.lines && invoiceItem.lines.data && invoiceItem.lines.data.length > 0) {
      for (const line of invoiceItem.lines.data) {
        // Process tax amounts if available
        if (line.tax_amounts && line.tax_amounts.length > 0) {
          for (const taxAmount of line.tax_amounts) {
            const taxRate = taxAmount.tax_rate ? (taxAmount.tax_rate.percentage || 0) : 0;
            const jurisdiction = taxAmount.tax_rate ? (taxAmount.tax_rate.jurisdiction || '') : '';
            const state = jurisdiction.includes('-') ? jurisdiction.split('-')[1] : jurisdiction;

            const key = `${period}|${country}|${state}|${taxRate}`;

            if (!taxSummary.has(key)) {
              taxSummary.set(key, {
                period,
                country,
                state,
                taxRate,
                taxableAmount: 0,
                taxCollected: 0
              });
            }

            const summary = taxSummary.get(key);
            summary.taxableAmount += (line.amount || 0) / 100; // Convert from cents
            summary.taxCollected += (taxAmount.amount || 0) / 100; // Convert from cents
          }
        } else {
          // Handle lines without tax (tax-exempt or zero-rate)
          const key = `${period}|${country}||0`;

          if (!taxSummary.has(key)) {
            taxSummary.set(key, {
              period,
              country,
              state: '',
              taxRate: 0,
              taxableAmount: 0,
              taxCollected: 0
            });
          }

          const summary = taxSummary.get(key);
          summary.taxableAmount += (line.amount || 0) / 100;
        }
      }
    } else {
      // Handle invoices without detailed line items
      const key = `${period}|${country}||0`;

      if (!taxSummary.has(key)) {
        taxSummary.set(key, {
          period,
          country,
          state: '',
          taxRate: 0,
          taxableAmount: 0,
          taxCollected: 0
        });
      }

      const summary = taxSummary.get(key);
      summary.taxableAmount += (invoiceItem.total || 0) / 100;
      summary.taxCollected += (invoiceItem.tax || 0) / 100;
    }
  }
}

// Format results for output
const result = Array.from(taxSummary.values()).map(summary => ({
  json: {
    period: summary.period,
    country: summary.country,
    state: summary.state || '',
    taxRate: summary.taxRate,
    taxableAmount: Math.round(summary.taxableAmount * 100) / 100,
    taxCollected: Math.round(summary.taxCollected * 100) / 100,
    processingDate: new Date().toISOString().split('T')[0]
  }
}));

console.log(`Generated ${result.length} tax summary records`);

return result.length > 0 ? result : [{ 
  json: { 
    message: 'No tax data found for the period',
    period: new Date().toISOString().substring(0, 7),
    processingDate: new Date().toISOString().split('T')[0]
  } 
}];

