const now = new Date();

// Ajustar a hora local de Colombia (UTC-5)
const utc = now.getTime() + now.getTimezoneOffset() * 60000;
const colombiaTime = new Date(utc - 5 * 3600000);

// Formato YYYY-MM-DD
const fecha = colombiaTime.toISOString().split('T')[0];

// Formato YYYY-MM-DD HH:mm:ss
const fechaHora = colombiaTime.toISOString().replace('T', ' ').substring(0, 19);

return [
  {
    json: {
      fecha_actual: fecha,
      fecha_hora_actual: fechaHora,
    }
  }
];

// Generar array del 1 al 10
const counter = Array.from({ length: 10 }, (_, i) => i + 1);

// Devolver el resultado en el formato adecuado
return [
  {
    json: {
      counter
    }
  }
];

const items = $input.all();
let counter = $input.first().json.counter;

return items.map(item => {
  const originalDate = new Date($('Validate if Date is in the past').item.json.output.date);
  originalDate.setDate(originalDate.getDate() - counter);
  const adjustedDate = originalDate.toISOString().split('T')[0];

  const result = {
    ...item.json,
    adjustedDate,
    loopCounter: counter
  };

  counter++;
  return { json: result };
});
