// This function processes each Excel row individually
for (const item of items) {
  // Create a formatted string representation of the complete row
  const rowData = {};

  // Copy all fields from the Excel row
  for (const key in item.json) {
    rowData[key] = item.json[key];
  }

  // Create a single JSON object for the entire row
  item.json = {
    data: JSON.stringify(rowData),
    rowData: rowData  // Keep the original data structure too
  };
}

return items;
