return items.map(item => {
  const rawUrl = item.json.website?.trim() || '';

    // If no scheme (http/https), prepend https://
  if (!/^https?:\/\//i.test(rawUrl)) {
    rawUrl = 'https://' + rawUrl;
  }

  try {
    const parsed = new URL(rawUrl);

    // Remove query parameters and fragments
    parsed.search = '';
    parsed.hash = '';

    item.json.cleanedWebsite = parsed.toString();
    item.json.websiteType = 'cleaned';  // optional tag
  } catch (err) {
    item.json.cleanedWebsite = null;
    item.json.websiteType = 'invalid';
    item.json.error = 'Invalid or missing URL';
  }

  return item;
});

// Regex to match emails (basic form)
const emailRegex = /[a-zA-Z0-9._%+-]+@[a-zA-Z0-9.-]+\.[a-z]{2,}/gi;

// Step 1: Collect all matches from all markdowns
let allEmails = [];

for (const item of items) {
  const markdown = item.json.data || '';
  const matches = markdown.match(emailRegex);

  if (matches && matches.length) {
    allEmails.push(...matches);
  }
}

// ✅ Step 2: Filter out fake emails that end with file extensions
const filteredEmails = allEmails.filter(email => 
  !email.toLowerCase().match(/\.(png|jpg|jpeg|gif|css|js)$/)
);

// Step 3: Remove duplicates
const uniqueEmails = [...new Set(filteredEmails)];

// Step 4: Return first email or "N/A"
return [
  {
    json: {
      email: uniqueEmails[0] || "N/A",
      allEmails: uniqueEmails.length ? uniqueEmails : ["N/A"]
    }
  }
];
