// N8N Code Node - Extract emails, LinkedIn, Facebook, Instagram for single item
const item = $input.first();

// Get the text content to search (adjust field names as needed)
const textContent = item.json.data.html;

// Initialize result object
const result = {
  website: $('Batch Processing Logic').first().json.website,
  emails: "None",
  linkedin: "None", 
  facebook: "None",
  instagram: "None",
  twitter: "None"
};

// Email extraction regex - matches common email patterns
const emailRegex = /\b[A-Za-z0-9._%+-]+@[A-Za-z0-9.-]+\.[A-Z|a-z]{2,}\b/gi;
const emails = textContent.match(emailRegex);

if (emails && emails.length > 0) {
  // Remove duplicates and filter out common non-email matches
  const uniqueEmails = [...new Set(emails)].filter(email => {
    const lowerEmail = email.toLowerCase();
    return !lowerEmail.includes('example.com') && 
           !lowerEmail.includes('test@') &&
           !lowerEmail.includes('noreply@') &&
           !lowerEmail.includes('no-reply@') &&
           lowerEmail.length > 5;
  });

  if (uniqueEmails.length > 0) {
    result.emails = uniqueEmails.length === 1 ? uniqueEmails[0] : uniqueEmails.join(', ');
  }
}

// LinkedIn extraction - improved pattern
const linkedinRegex = /(?:https?:\/\/)?(?:www\.)?linkedin\.com\/(?:in\/|company\/|pub\/)[a-zA-Z0-9\-._]+\/?/gi;
const linkedinMatches = textContent.match(linkedinRegex);
if (linkedinMatches && linkedinMatches.length > 0) {
  const cleanLinkedin = linkedinMatches[0].replace(/^(?:https?:\/\/)?(?:www\.)?/i, 'https://www.');
  result.linkedin = cleanLinkedin;
}

// Facebook extraction - improved to match numeric IDs and usernames
const facebookRegex = /(?:https?:\/\/)?(?:www\.|m\.|mobile\.)?facebook\.com\/(?:[^\/\s]+\/)*[^\/\s?#]+/gi;
const facebookMatches = textContent.match(facebookRegex);
if (facebookMatches && facebookMatches.length > 0) {
  const cleanFacebook = facebookMatches[0].replace(/^(?:https?:\/\/)?(?:www\.)?/i, 'https://www.');
  result.facebook = cleanFacebook;
}

// Instagram extraction - improved to match various URL formats
const instagramRegex = /(?:https?:\/\/)?(?:www\.)?instagram\.com\/[a-zA-Z0-9\-._]+\/?/gi;
const instagramMatches = textContent.match(instagramRegex);
if (instagramMatches && instagramMatches.length > 0) {
  const cleanInstagram = instagramMatches[0].replace(/^(?:https?:\/\/)?(?:www\.)?/i, 'https://www.');
  result.instagram = cleanInstagram;
}

// Twitter/X extraction - improved to match usernames properly
const twitterRegex = /(?:https?:\/\/)?(?:www\.)?(?:twitter\.com|x\.com)\/[a-zA-Z0-9_]+\/?/gi;
const twitterMatches = textContent.match(twitterRegex);
if (twitterMatches && twitterMatches.length > 0) {
  const cleanTwitter = twitterMatches[0].replace(/^(?:https?:\/\/)?(?:www\.)?/i, 'https://www.');
  result.twitter = cleanTwitter;
}

// Alternative extraction for social handles without full URLs
// Look for @username patterns for Instagram and Twitter
if (result.instagram === "None") {
  const igHandleRegex = /@([a-zA-Z0-9._]{1,30})/gi;
  const igHandles = textContent.match(igHandleRegex);
  if (igHandles && igHandles.length > 0) {
    const username = igHandles[0].replace('@', '');
    if (username.length > 0 && !username.includes(' ')) {
      result.instagram = `https://www.instagram.com/${username}`;
    }
  }
}

// Look for Twitter handles without full URLs
if (result.twitter === "None") {
  const twitterHandleRegex = /@([a-zA-Z0-9_]{1,15})/gi;
  const twitterHandles = textContent.match(twitterHandleRegex);
  if (twitterHandles && twitterHandles.length > 0) {
    const username = twitterHandles[0].replace('@', '');
    if (username.length > 0 && !username.includes(' ')) {
      result.twitter = `https://www.x.com/${username}`;
    }
  }
}

// Clean up URLs - remove trailing slashes and parameters
['linkedin', 'facebook', 'instagram', 'twitter'].forEach(platform => {
  if (result[platform] !== "None") {
    result[platform] = result[platform].split('?')[0].split('#')[0].replace(/\/$/, '');
  }
});

return result;
