const body = $input.first().json.body;

const ip = body?.result?.src_ip || 'No IP found';
const reason = body?.result?.reason || 'No reason found';

return [
  {
    json: {
      ip_address: ip,
      description: reason
    }
  }
];

let virustotal = null;
let alienvault = null;
let metadata = null;

for (const item of items) {
  if (item.json?.data?.attributes) {
    // Likely VirusTotal result
    virustotal = item.json;
  } else if (item.json?.pulse_info) {
    // Likely AlienVault result
    alienvault = item.json;
  } else if (item.json?.description) {
    // The Wazuh description or IP metadata
    metadata = item.json;
  }
}

return [{
  json: {
    virustotal,
    alienvault,
    description: metadata?.description || "No description",
    ip_address: metadata?.ip_address || virustotal?.data?.id || "Unknown IP"
  }
}];

// Get merged input (single item array)
const data = items[0].json;

// VirusTotal extraction
const vt = data.virustotal?.data?.attributes;
const vtTagsArray = vt?.tags || [];

const malicious = vt?.last_analysis_stats?.malicious || 0;
const suspicious = vt?.last_analysis_stats?.suspicious || 0;

const vtSummary = vt ? {
  IP: data.virustotal.data.id,
  Reputation: vt.reputation,
  Tags: vtTagsArray.join(', '),
  Tags_HTML: vtTagsArray.map(tag =>
    `<span class="tag">${tag.trim()}</span>`
  ).join(''),
  Harmless: vt.last_analysis_stats?.harmless || 0,
  Malicious: malicious,
  Suspicious: suspicious,
  Undetected: vt.last_analysis_stats?.undetected || 0,
  Whois: vt.whois?.split("\n")[0] || "No WHOIS info",
  Description: data.description || "No description"
} : {};

// AlienVault extraction with fallback
let avSummary = {};
let isAlienVaultSuspicious = false;

if (data.alienvault && data.alienvault.pulse_info) {
  const av = data.alienvault;
  const pulseNamesArray = (av?.pulse_info?.pulses || []).map(p => p.name);
  const pulseCount = av?.pulse_info?.count || 0;

  isAlienVaultSuspicious = pulseCount > 0;

  avSummary = {
    IP: av.indicator,
    Reputation: av.reputation,
    Whois: av.whois,
    Pulse_Count: pulseCount,
    Pulse_Names: pulseNamesArray.join(', '),
    Pulse_Names_HTML: pulseNamesArray.map(name =>
      `<span class="tag">${name.trim()}</span>`
    ).join('')
  };
} else {
  avSummary = {
    IP: 'N/A (Private IP)',
    Reputation: 'N/A',
    Whois: 'N/A',
    Pulse_Count: 0,
    Pulse_Names: 'N/A',
    Pulse_Names_HTML: ''
  };
}

// Global Status Logic
const isVirusTotalSuspicious = malicious > 0 || suspicious > 0;
const finalStatus = (isVirusTotalSuspicious || isAlienVaultSuspicious) ? 'Suspicious' : 'Safe';

// Timestamp
const generatedAt = new Intl.DateTimeFormat('en-IN', {
  dateStyle: 'short',
  timeStyle: 'medium',
  timeZone: 'Asia/Kolkata'
}).format(new Date());

// Final Output
return [
  {
    json: {
      summary: {
        VirusTotal: vtSummary,
        AlienVault: avSummary,
        Status: finalStatus,
        Generated_At: generatedAt
      }
    }
  }
];
