// Node: Code (Parse DOI)
const txt = $json.message?.text?.trim() || "";

function extractDOI(s) {
  // 1) doi.org/...
  let m = s.match(/doi\.org\/(10\.\d{4,9}\/\S+)/i);
  if (m) return m[1].replace(/[)\].,;]*$/, "");
  // 2) raw DOI
  m = s.match(/(10\.\d{4,9}\/\S+)/i);
  if (m) return m[1].replace(/[)\].,;]*$/, "");
  // 3) arXiv link
  m = s.match(/arxiv\.org\/abs\/([\w\-.]+)/i);
  if (m) return `10.48550/arXiv.${m[1]}`;
  return null;
}

const doi = extractDOI(txt);
if (!doi) {
  return [{ error: true, message: "DOI not found. Please send a DOI or arXiv/doi.org link." }];
}

return [{ doi }];

// Code4 ‚Äî Build Zotero parent metadata + pick best PDF URL
// Input from Merge: $json.crossref, $json.datacite, $json.unpay, and $json.doi

const doi = $json.doi;

// Unwrap API results if not error
const crossrefBody = ($json.crossref && !$json.crossref.error) ? $json.crossref.message : null;
const dataciteAttr = ($json.datacite && !$json.datacite.error) ? $json.datacite.data?.attributes : null;
const unpayBody    = ($json.unpay && !$json.unpay.error) ? $json.unpay : null;

const pick = (...vals) => vals.find(v => {
  if (v === undefined || v === null) return false;
  if (typeof v === 'string') return v.trim().length > 0;
  return true;
}) ?? null;

/* ---------- PDF pickers ---------- */
function fromCrossref(m) {
  if (!m) return null;
  const links = m.link || [];
  for (const l of links) {
    const ct  = (l['content-type'] || '').toLowerCase();
    const url = l.URL || '';
    if (ct.includes('pdf') || /\.pdf(\?|$)/i.test(url)) return url;
  }
  return null;
}

function fromDataCite(attr) {
  if (!attr) return null;
  // try identifiers -> arXiv
  const ids = attr.identifiers || [];
  const arxiv = ids.find(i => /arxiv/i.test(i.identifierType || ''))?.identifier;
  if (arxiv) {
    const id = String(arxiv).replace(/^arXiv:/i,'');
    return `https://arxiv.org/pdf/${id}.pdf`;
  }
  return null;
}

function fromUnpay(u) {
  if (!u) return null;
  if (u.best_oa_location?.url_for_pdf) return u.best_oa_location.url_for_pdf;
  if (/\.pdf(\?|$)/i.test(u.best_oa_location?.url || '')) return u.best_oa_location.url;
  for (const loc of (u.oa_locations || [])) {
    if (loc.url_for_pdf) return loc.url_for_pdf;
    if (/\.pdf(\?|$)/i.test(loc.url || '')) return loc.url;
  }
  return null;
}

// Priority: Crossref ‚Üí Unpaywall ‚Üí DataCite ‚Üí arXiv fallback (from DOI)
let pdfUrl = fromCrossref(crossrefBody);
if (!pdfUrl) pdfUrl = fromUnpay(unpayBody);
if (!pdfUrl) pdfUrl = fromDataCite(dataciteAttr);
if (!pdfUrl && /^10\.48550\/arxiv\./i.test(doi || '')) {
  const arxivId = doi.replace(/^10\.48550\/arxiv\./i, '');
  pdfUrl = `https://arxiv.org/pdf/${arxivId}.pdf`;
}

/* ---------- Parent metadata (Zotero item) ---------- */
// Title
const title = pick(
  crossrefBody?.title?.[0],
  dataciteAttr?.titles?.[0]?.title,
  doi
);

// Date (YYYY, YYYY-MM, or YYYY-MM-DD)
const issued = crossrefBody?.issued?.['date-parts']?.[0] || [];
const dateStr = issued.length
  ? issued.join('-')
  : pick(
      dataciteAttr?.published,
      dataciteAttr?.publishedOnline,
      dataciteAttr?.publishedPrint,
      dataciteAttr?.created
    ) || "";

// Journal / container
const publicationTitle = pick(
  crossrefBody?.['container-title']?.[0],
  dataciteAttr?.container?.title
) || "";

// Landing page (not PDF)
const landingFromCR = crossrefBody?.URL;
const landingFromUP = unpayBody?.best_oa_location?.url || unpayBody?.oa_locations?.[0]?.url;
const landingFromDC = dataciteAttr?.url;
const url = pick(landingFromCR, landingFromUP, landingFromDC) || "";

// Abstract
let abstractNote = "";
if (Array.isArray(dataciteAttr?.descriptions) && dataciteAttr.descriptions.length) {
  const absExact = dataciteAttr.descriptions.find(d => /abstract/i.test(d.descriptionType || ''));
  abstractNote = pick(absExact?.description, dataciteAttr.descriptions[0]?.description) || "";
}

// Creators
let creators = [];
if (Array.isArray(crossrefBody?.author)) {
  creators = crossrefBody.author.map(a => ({
    creatorType: "author",
    firstName: a.given || "",
    lastName:  a.family || (a.name || "")
  }));
} else if (Array.isArray(dataciteAttr?.creators)) {
  creators = dataciteAttr.creators.map(c => ({
    creatorType: "author",
    firstName: c.givenName || "",
    lastName:  c.familyName || (c.name || "")
  }));
}

// Tags
const tags = [];
if (Array.isArray(crossrefBody?.subject)) {
  for (const s of crossrefBody.subject.slice(0, 5)) tags.push({ tag: String(s) });
}
tags.push({ tag: "from-telegram" });

// Item type heuristic
const itemType = publicationTitle ? "journalArticle" : "preprint";

// Build meta object for POST /items (parent)
const meta = {
  itemType,
  title,
  DOI: doi || "",
  url,
  date: dateStr,
  publicationTitle,
  abstractNote,
  creators,
  tags
};

// Unified output for next step:
// - meta: used for "Zotero - Create Parent"
// - pdfUrl + hasPdf: for IF & attachment creation (linked_url)
return [{
  doi,
  title,
  meta,
  pdfUrl,
  hasPdf: Boolean(pdfUrl)
}];

