// Collect all media IDs from the loop
const items = $input.all();

// Extract media IDs and other data
const mediaIds = [];
let commonData = {};

for (const item of items) {
 if (item.json.media_id) {
   mediaIds.push(item.json.media_id);
 }

 // Store common data from first item
 if (Object.keys(commonData).length === 0) {
   commonData = {
     model: item.json.model,
     instagram_content: item.json.instagram_content,
     "Instagram Account ID": item.json["Instagram Account ID"],
     "Facebook Graph": item.json["Facebook Graph"],
     "airtable id": item.json["airtable id"]
   };
 }
}

// Return collected data
return [{
 json: {
   ...commonData,
   media_ids: mediaIds,
   total_media_count: mediaIds.length
 }
}];
