const newItems = [];

for (const key of Object.keys(items[0].binary)) {
  const binaryData = items[0].binary[key];

  // Check if the file is a PDF
  if (binaryData.mimeType === "application/pdf") {
    newItems.push({
      json: { fileName: key },
      binary: {
        data: binaryData
      }
    });
  }
}

return newItems;
