// Filter to get only new threads (not replies)
const items = $input.all();
const newThreads = [];

for (const item of items) {
  const title = item.json.title;
  const link = item.json.link;

  // Check if title starts with "Re:" 
  // This is the most reliable indicator of a reply
  if (!title.startsWith('Re:')) {

    // Extract topic ID for tracking
    const topicMatch = link.match(/topic=(\d+)/);
    if (topicMatch) {
      item.json.topicId = topicMatch[1];
      newThreads.push(item);
    }
  }
}

return newThreads;

// Get the HTML content from current input
const htmlContent = $json.html; // Adjust this path based on your data structure

if (!htmlContent) {
  return {}; // Return empty if no HTML content
}

// Use regex to extract all img tags and their attributes
const imgRegex = /<img[^>]*>/gi;
const srcRegex = /src\s*=\s*["']([^"']*)["']/i;
const altRegex = /alt\s*=\s*["']([^"']*)["']/i;
const classRegex = /class\s*=\s*["']([^"']*)["']/i;
const widthRegex = /width\s*=\s*["']?([^"'\s>]*)["']?/i;
const heightRegex = /height\s*=\s*["']?([^"'\s>]*)["']?/i;

// Find all img tags
const imgTags = htmlContent.match(imgRegex) || [];

// Extract image data
const images = imgTags.map((imgTag, index) => {
  const srcMatch = imgTag.match(srcRegex);
  const altMatch = imgTag.match(altRegex);
  const classMatch = imgTag.match(classRegex);
  const widthMatch = imgTag.match(widthRegex);
  const heightMatch = imgTag.match(heightRegex);

  return {
    index: index + 1,
    src: srcMatch ? srcMatch[1] : '',
    alt: altMatch ? altMatch[1] : '',
    class: classMatch ? classMatch[1] : '',
    width: widthMatch ? widthMatch[1] : '',
    height: heightMatch ? heightMatch[1] : '',
    fullTag: imgTag
  };
});

// Filter out images without src
const validImages = images.filter(img => img.src);

// Return the results as a single object
return {
  json: {
    totalImages: validImages.length,
    images: validImages
  }
};

// Get the data from previous nodes
const threadData = $('Filter New Threads').item.json;
const images = $json.images || [];

// Create the base embed with all metadata
const baseEmbed = {
  title: threadData.title,
  url: threadData.link,
  color: 5814783,
  footer: {
    text: threadData.categories[0]
  },
  timestamp: threadData.isoDate
};

// Create embeds array
const embeds = [];

// Limit to max 4 images
const maxImages = Math.min(images.length, 4);

for (let i = 0; i < maxImages; i++) {
  if (i === 0) {
    // First embed gets all the metadata + first image
    embeds.push({
      ...baseEmbed,
      image: {
        url: images[i].src
      }
    });
  } else {
    // Additional embeds only get URL and image (for multi-image merge)
    embeds.push({
      description: "Additional images",
      url: threadData.link,
      image: {
        url: images[i].src
      }
    });
  }
}

// If no images, create single embed without image
if (images.length === 0) {
  embeds.push(baseEmbed);
}

// Create final webhook payload
const webhookPayload = {
  username: "Geekhack Updates",
  embeds: embeds
};

// Return the payload for the next node
return {
  json: webhookPayload
};
