// Extract domain from email and convert to website URL
const email = $input.item.json['Work Email']; // Adjust this path based on your input data structure

// Extract domain from email (part after @)
const domain = email.split('@')[1];

// Convert to website URL (assuming https)
const websiteUrl = `https://${domain}`;

$input.item.json.domain = domain
$input.item.json.websiteUrl = websiteUrl

// Return the result
return $input.item;

// Flag items based on common email providers
const commonProviders = [
  'gmail', 'yahoo', 'ymail', 'rocketmail',
  'outlook', 'hotmail', 'live', 'msn',
  'icloud', 'me', 'mac', 'aol',
  'zoho', 'protonmail', 'mail', 'gmx'
];

// Get the domain from the input
const domain = $input.item.json.domain;

// Extract the main domain part (remove .com, .net, etc.)
const domainParts = domain.split('.');
const mainDomain = domainParts[0].toLowerCase();

// Check if it's a common provider
const isCommonProvider = commonProviders.includes(mainDomain);

$input.item.json.isPersonalEmail = isCommonProvider;

return $input.item;
