parameters.jsCode:
let linearStatus;
// Check if URL contains "/project/" if not is an issue
if ($json.url.includes('/project/')) {
  linearStatus = $json.data.status.name;
} else {
  linearStatus =$json.data.state.type;
}
let productboardStatus;

switch(linearStatus) {
  case 'Backlog':
    productboardStatus = 'Candidate';
    break;
  case 'backlog':
    productboardStatus = 'Candidate';
    break;
  case 'triage':
    productboardStatus = 'Candidate';
    break;
  case 'Planned':
    productboardStatus = 'Planned';
    break;
  case 'Paused':
    productboardStatus = 'Planned';
    break;
  case 'unstarted':
    productboardStatus = 'Planned';
    break;
  case 'In Progress':
    productboardStatus = 'In progress';
    break;
  case 'started':
    productboardStatus = 'In progress';
    break;
  case 'completed':
    productboardStatus = 'Released';
    break;
  case 'Completed':
    productboardStatus = 'Released';
    break;
  case 'Canceled':
    productboardStatus = 'Won\'t do';
    break;
  case 'canceled':
    productboardStatus = 'Won\'t do';
    break;
  default:
    productboardStatus = 'Unknown'; // Error
}

return {
  ...$json,
  linear_status_mapped: productboardStatus
};

parameters.jsonBody:
={
  "data": {
    "status": {
      "name": "{{ $('mapping Linear status to Productboard status').item.json.linear_status_mapped }}"
    },
    "timeframe": {
      {{ $('mapping Linear status to Productboard status').item.json.data.targetDate ? '"granularity": "month",': '"granularity": "none",'}}
      {{ $('mapping Linear status to Productboard status').item.json.data.targetDate ? '"startDate": "' + $('mapping Linear status to Productboard status').item.json.data.targetDate.substring(0, 7) + '-01' +'",': '"startDate": "none",'}}
      {{ $('mapping Linear status to Productboard status').item.json.data.targetDate 
        ? (() => {
            const date = new Date($('mapping Linear status to Productboard status').item.json.data.targetDate);
            const year = date.getFullYear();
            const month = date.getMonth() + 1;
            const lastDay = new Date(year, month, 0).getDate();
            return `"endDate": "${year}-${month.toString().padStart(2, '0')}-${lastDay}"`;
          })() 
        : '"endDate": "none"'}}
    }
  }
}

