const baseUrl = "https://www.playway.com";

return $input.first().json.pdf.map(link => {
  return {
    json: {
      url: `${baseUrl}${link}`
    }
  };
});

const input = $input.all();
const chunkSize = 1900;
const result = [];

for (const item of input) {
  const text = item.json.output;
  for (let i = 0; i < text.length; i += chunkSize) {
    result.push({
      json: {
        message: text.slice(i, i + chunkSize)
      }
    });
  }
}

return result;
