// Parse the AI response and extract only the JSON part
let aiResponse = $input.first().json.output;

// استخراج النص بين أول "[" وآخر "]" ليصبح JSON صالح
const jsonStart = aiResponse.indexOf('[');
const jsonEnd = aiResponse.lastIndexOf(']');
if (jsonStart === -1 || jsonEnd === -1) {
  return [{
    json: {
      error: 'No JSON found in AI response',
      raw_response: aiResponse
    }
  }];
}

const jsonString = aiResponse.slice(jsonStart, jsonEnd + 1);

try {
  const workplaceReports = JSON.parse(jsonString);
  const outputItems = workplaceReports.map((report, index) => ({
    json: {
      workplace: report.workplace,
      month: report.month,
      total_hours: report.total_hours,
      total_days: report.total_days,
      average_daily_hours: report.average_daily_hours,
      detailed_days: report.detailed_days,
      index: index
    }
  }));
  return outputItems;
} catch (error) {
  console.error('Error parsing AI response:', error);
  return [{
    json: {
      error: 'Failed to parse workplace reports',
      raw_response: aiResponse
    }
  }];
}

// استدعاء البيانات من الـ input
const workplaceData = $input.first().json;
if (workplaceData.error) {
  return [{ json: workplaceData }];
}

// نبني الصفوف
const rows = workplaceData.detailed_days.map(day => `
  <tr>
    <td>${day.date}</td>
    <td>${day.start_time}</td>
    <td>${day.end_time}</td>
    <td>${day.hours_worked}</td>
    <td>${day.notes || "-"}</td>
  </tr>
`).join("");

// HTML مع ستايل فخم
const htmlContent = `
<!DOCTYPE html>
<html lang="ar">
<head>
  <meta charset="UTF-8">
  <title>تقرير ${workplaceData.workplace} - ${workplaceData.month}</title>
  <style>
    body {
      font-family: "Tahoma", "Arial", sans-serif;
      direction: rtl;
      text-align: right;
      background: #f9f9f9;
      color: #333;
      margin: 40px;
      line-height: 1.6;
    }
    h1 {
      color: #2c3e50;
      text-align: center;
      border-bottom: 3px solid #3498db;
      padding-bottom: 10px;
      margin-bottom: 30px;
    }
    h2 {
      margin-top: 30px;
      color: #34495e;
    }
    p {
      font-size: 16px;
      margin: 5px 0;
    }
    table {
      width: 100%;
      border-collapse: collapse;
      margin-top: 20px;
      background: #fff;
      box-shadow: 0 2px 6px rgba(0,0,0,0.1);
      border-radius: 6px;
      overflow: hidden;
    }
    th {
      background: #3498db;
      color: #fff;
      font-weight: bold;
      font-size: 14px;
      padding: 12px;
    }
    td {
      border: 1px solid #ddd;
      padding: 10px;
      font-size: 14px;
    }
    tr:nth-child(even) { background: #f2f9ff; }
    tr:hover { background: #eaf6ff; }
  </style>
</head>
<body>
  <h1>📊 تقرير ساعات العمل - ${workplaceData.workplace}</h1>

  <p><b>📅 الشهر:</b> ${workplaceData.month}</p>
  <p><b>⏱️ إجمالي الساعات:</b> ${workplaceData.total_hours}</p>
  <p><b>📆 عدد الأيام:</b> ${workplaceData.total_days}</p>
  <p><b>⚖️ المتوسط اليومي:</b> ${workplaceData.average_daily_hours}</p>

  <h2>تفاصيل الأيام:</h2>
  <table>
    <thead>
      <tr>
        <th>التاريخ</th>
        <th>بداية</th>
        <th>نهاية</th>
        <th>عدد الساعات</th>
        <th>ملاحظات</th>
      </tr>
    </thead>
    <tbody>
      ${rows}
    </tbody>
  </table>
</body>
</html>
`;

return [{
  json: {
    workplace: workplaceData.workplace,
    month: workplaceData.month,
    htmlContent: htmlContent,
    fileName: `تقرير_${workplaceData.workplace}_${workplaceData.month}.pdf`
  }
}];

// ناخذ الـ item من الـ input (النود السابق)
const item = $input.first().json;

// نقرأ الـ HTML
const html = item.htmlContent;

// نرجع الناتج: JSON + Binary
return [
  {
    json: {
      workplace: item.workplace,
      month: item.month,
      fileName: item.fileName
    },
    binary: {
      data: {
        data: Buffer.from(html).toString("base64"),
        mimeType: "text/html",
        fileName: "document.html" // ملف مؤقت، بعد التحويل بيصير PDF
      }
    }
  }
];
