=<!DOCTYPE html>
<html lang="en">
<head>
  <meta charset="UTF-8" />
  <meta name="viewport" content="width=device-width, initial-scale=1.0" />
  <title>AI CV Optimizer</title>
  <style>
    :root {
      --primary: #2563eb;
      --primary-hover: #1e4ed8;
      --gray-light: #f9fafb;
      --gray: #ccc;
      --text-dark: #333;
      --radius: 10px;
    }
    body {
      font-family: Arial, sans-serif;
      background: var(--gray-light);
      margin: 0;
      padding: 0;
      display: flex;
      justify-content: center;
      align-items: center;
      min-height: 100vh;
    }
    .container {
      background: white;
      padding: 2rem;
      border-radius: var(--radius);
      box-shadow: 0 6px 16px rgba(0,0,0,0.1);
      max-width: 420px;
      width: 100%;
    }
    h2 {
      margin-bottom: 1.5rem;
      color: var(--text-dark);
      font-size: 1.5rem;
      text-align: center;
    }
    label {
      font-weight: 600;
      display: block;
      margin: 1rem 0 0.5rem;
    }
    input[type="file"], input[type="text"], input[type="email"] {
      width: 100%;
      padding: 0.75rem;
      border: 1px solid var(--gray);
      border-radius: var(--radius);
      font-size: 0.95rem;
      box-sizing: border-box;
    }
    input:focus {
      border-color: var(--primary);
      outline: none;
      box-shadow: 0 0 0 3px rgba(37,99,235,0.15);
    }
    button {
      margin-top: 1.5rem;
      padding: 0.9rem;
      background: var(--primary);
      color: white;
      border: none;
      border-radius: var(--radius);
      width: 100%;
      font-size: 1rem;
      font-weight: 600;
      cursor: pointer;
      transition: background 0.2s ease;
    }
    button:hover {
      background: var(--primary-hover);
    }
    button:disabled {
      background: #9ca3af;
      cursor: not-allowed;
    }
    .status {
      margin-top: 1rem;
      font-size: 0.9rem;
      text-align: center;
    }
    .status.success { color: green; }
    .status.error { color: red; }
  </style>
</head>
<body>
  <div class="container">
    <h2>AI CV Optimizer</h2>
    <form id="cvForm">
      <label for="cv">Upload your CV (PDF):</label>
      <input type="file" id="cv" name="cv" accept="application/pdf" required />

      <label for="job_url">Job Posting URL:</label>
      <input type="text" id="job_url" name="job_url" placeholder="https://linkedin.com/job/123" required />

      <label for="email">Your Email:</label>
      <input type="email" id="email" name="email" placeholder="you@example.com" required/>

      <button type="submit">Check My CV</button>
      <div class="status" id="status"></div>
    </form>
  </div>

  <script>
    const form = document.getElementById("cvForm");
    const statusDiv = document.getElementById("status");

    form.addEventListener("submit", async (e) => {
      e.preventDefault();

      const formData = new FormData(form);
      statusDiv.textContent = "⏳ Uploading and analyzing your CV...";
      statusDiv.className = "status";

      try {
        const res = await fetch("https://n8nworkflow.eu/webhook-test/cv-optimizer", {
          method: "POST",
          body: formData
        });

        if (!res.ok) {
          throw new Error("Server error: " + res.statusText);
        }

        const data = await res.json();
        console.log("n8n response:", data);
        statusDiv.textContent = "✅ CV submitted successfully! Check your email for results.";
        statusDiv.className = "status success";
      } catch (err) {
        console.error(err);
        statusDiv.textContent = "❌ Error: " + err.message;
        statusDiv.className = "status error";
      }
    });
  </script>
</body>
</html>

