const raw = $json.data || "";
const text = raw
  .replace(/<script[\s\S]*?<\/script>/gi, "")
  .replace(/<style[\s\S]*?<\/style>/gi, "")
  .replace(/<\/?[^>]+(>|$)/g, " ")
  .replace(/\s+/g, " ")
  .trim();
return [{ job_text: text }];
