const msg = $json.message || '';
const wordCount = msg.split(/\s+/).filter(w => w).length;
const { minWords, waitLong, waitShort } = items[0].json;
const waitSeconds = wordCount < minWords ? waitShort : waitLong;
return [{ json: { context_id: $json.context_id, message: msg, waitSeconds } }];

// Run Once For All Items
const j = items[0].json;
const raw = Array.isArray(j.buffer) ? j.buffer : [];

// 1) Parsear cada entrada lo m√°s flexible posible
const buffer = raw
  .map(x => {
    let obj;
    // Si viene como string JSON, lo parseo
    if (typeof x === 'string') {
      try {
        obj = JSON.parse(x);
      } catch {
        return null;
      }
    } else if (typeof x === 'object') {
      obj = x;
    } else {
      return null;
    }

    // 2) Extraer texto de message, sea string o anidado { text }
    let textVal;
    if (typeof obj.message === 'string') {
      textVal = obj.message;
    } else if (obj.message && typeof obj.message.text === 'string') {
      textVal = obj.message.text;
    } else {
      return null;
    }

    // 3) Validar timestamp
    if (!obj.timestamp) return null;

    return {
      text: textVal.trim(),
      timestamp: obj.timestamp,
    };
  })
  .filter(Boolean);

// 4) Orden cronol√≥gico
buffer.sort((a, b) => new Date(a.timestamp) - new Date(b.timestamp));

// 5) Dedupe y concatenar
const unique = [...new Set(buffer.map(m => m.text))];
const consolidated = unique.join(' ');

// 6) Devolver
return [{
  json: {
    context_id: j.context_id,
    message: consolidated,
  }
}];
