// Gestisci sia array che singoli oggetti
let input = $input.first().json;

// Debug iniziale
console.log("Input raw:", JSON.stringify(input, null, 2));

// Se l'input è un array, prendi il primo elemento
if (Array.isArray(input)) {
  console.log("Input è un array, prendo il primo elemento");
  input = input[0];
}

// Verifica che vectors esista ed è un array
if (!input.vectors || !Array.isArray(input.vectors)) {
  console.error("Struttura input:", {
    hasVectors: input.hasOwnProperty('vectors'),
    vectorsType: typeof input.vectors,
    keys: Object.keys(input)
  });
  throw new Error(`Il campo 'vectors' è mancante o non è un array. Input ricevuto: ${JSON.stringify(input, null, 2)}`);
}

// Verifica che ci siano vettori
if (input.vectors.length === 0) {
  throw new Error("L'array 'vectors' è vuoto.");
}

// Estrai gli ID
const ids = input.vectors.map(vector => vector.id);

// Restituisci l'oggetto per il fetch
return [{
  ids: ids,
  namespace: input.namespace || "jira",
}];

const vectors = items[0].json.vectors;
const targetClass = $('Parameters').first().json.targetCollection

return Object.entries(vectors).map(([key, vectorObj]) => {
  const { id, values, metadata } = vectorObj;

  return {
    json: {
      class: targetClass,
      id: id,
      vector: values,
      properties: {
        issue_customer: metadata.issue_customer,
        issue_id: metadata.issue_id,
        issue_key: metadata.issue_key,
        summary: metadata.summary,
        text: metadata.text,
        triage: metadata.triage
      }
    }
  };
});
