// Get the current date and time
const now = new Date();

// Set the threshold (in days) for SSL expiration warning
const thresholdDays = 14;

// Filter the input data to only include entries where the SSL certificate
// expires within the threshold (i.e., 14 days or fewer from today)
const filtered = $input.all().filter(item => {
  const expiry = new Date(item.json["SSL Expired On"]);
  const daysLeft = (expiry - now) / (1000 * 60 * 60 * 24); // Convert milliseconds to days
  return daysLeft <= thresholdDays;
});

// If no certificates are expiring soon, return a simple "All Good" message
if (filtered.length === 0) {
  return [
    {
      json: {
        message: "All Good"
      }
    }
  ];
} else {
  // If some certificates are expiring soon, build a message listing them
  const websiteNames = filtered.map(entry => {
    const daysLeft = Math.ceil((new Date(entry.json["SSL Expired On"]) - now) / (1000 * 60 * 60 * 24));
    return `- ${entry.json.Name} (expires in ${daysLeft} days)`;
  }).join('\n');

  // Create the final message to return
  const message = `SSL certificates expiring soon:\n${websiteNames}`;

  return [
    {
      json: {
        message: message
      }
    }
  ];
}
