// Get the raw, deeply nested string output from the AI node
const aiRawOutput = $json.content.parts[0].text;

// Use a regular expression to extract the JSON string part enclosed in ```
const jsonMatch = aiRawOutput.match(/\{[\s\S]*\}/);

// Check if a JSON string was successfully found
if (jsonMatch && jsonMatch[0]) {
  try {
    // Parse the extracted pure JSON string into a real JSON object
    const parsedData = JSON.parse(jsonMatch[0]);

    // Return the parsed object as the output of this node
    return {
      json: parsedData
    };
  } catch (error) {
    // If parsing fails, return an error message
    return { json: { error: "Failed to parse JSON", details: error.message } };
  }
}

// If no JSON string is found, also return an error message
return { json: { error: "JSON block not found in AI output" } };

// Get all items passed from the previous node
const items = $input.all();

// If there is no input data, return an empty array to stop the workflow here
if (items.length === 0) {
  return [];
}

// Use the reduce method to iterate through all items and find the latest date
// We start with a very early date as the initial comparison value
const initialValue = { json: { isoDate: '1970-01-01T00:00:00.000Z' } };

const latestItem = items.reduce((latest, current) => {
  // ISO format date strings can be compared directly
  if (current.json.isoDate > latest.json.isoDate) {
    return current;
  } else {
    return latest;
  }
}, initialValue);

// Extract the isoDate from the found latest item
const latestTimestamp = latestItem.json.isoDate;

// Return the result in the standard n8n format
// This result can be easily used by subsequent nodes (like IF or Set nodes)
return [
  {
    json: {
      latestTimestamp: latestTimestamp
    }
  }
];

