// Force to Kyiv timezone
const now = new Date();

// Format to Kyiv and extract hour
const formatter = new Intl.DateTimeFormat('en-GB', {
  timeZone: 'Europe/Kyiv',
  hour: 'numeric',
  hour12: false
});

const hour = parseInt(formatter.format(now), 10);

// Active hours: 07:00–23:59
if (hour < 7 || hour >= 24) {
  return [{ json: { value: false } }];
}

// Probability (~28%)
if (Math.random() < 1) {
  return [{ json: { value: true } }];
} else {
  return [{ json: { value: false } }];
}

