const output = $json.output;

if (output.startsWith('READY_TO_GENERATE')) {
  const prompt = output.replace('READY_TO_GENERATE: ', '');
  return [{
    json: {
      type: 'image',
      prompt: prompt,
    }
  }];
} else {
  return [{
    json: {
      type: 'text',
      text: output,
    }
  }];
}

const response = $json;

if (response.candidates?.[0]?.content?.parts) {
  const imagePart = response.candidates[0].content.parts.find(part => part.inlineData);
  if (imagePart) {
    // Convert base64 to binary buffer
    const binaryData = Buffer.from(imagePart.inlineData.data, 'base64');

    return [{
      json: {
        success: true
      },
      binary: {
        photo: {
          data: binaryData,
          mimeType: 'image/jpeg',
          fileName: 'generated_image.jpg'
        }
      }
    }];
  }
}

return [{ json: { error: "Failed" } }];
