// ---- helpers ----
function slugify(s) {
  return String(s || '')
    .toLowerCase()
    .normalize('NFD').replace(/[\u0300-\u036f]/g, '')
    .replace(/[^a-z0-9]+/g, '-')
    .replace(/^-+|-+$/g, '')
    .slice(0, 60);
}

function extractSources(html) {
  if (!html) return '';
  if (/<h2[^>]*>Sources<\/h2>/i.test(html)) return html;

  const urls = Array.from(new Set(
    (html.match(/https?:\/\/[^\s"')<>]+/g) || []).map(u => u.replace(/[.,)\]]+$/,''))
  ));

  if (!urls.length) return html;

  const items = urls.map(u => {
    try {
      const host = new URL(u).hostname.replace(/^www\./,'');
      return `<li><a href="${u}">${host}</a></li>`;
    } catch {
      return `<li><a href="${u}">${u}</a></li>`;
    }
  }).join('');

  return html + `\n\n<h2>Sources</h2><ul>${items}</ul>`;
}

// Detect object vs string safely and only JSON.parse when it's a real string
function coerceObject(j) {
  // 1) Common places where the node already gives an OBJECT
  if (j?.message?.parsed && typeof j.message.parsed === 'object') return j.message.parsed;
  if (j?.choices?.[0]?.message?.parsed && typeof j.choices[0].message.parsed === 'object') {
    return j.choices[0].message.parsed;
  }
  if (j?.message?.content && typeof j.message.content === 'object') return j.message.content;

  // 2) Candidate content (could be string or object)
  let cand =
    j?.choices?.[0]?.message?.content ??
    j?.message?.content ??
    j?.content ??
    '';

  // If it's already an OBJECT, just use it
  if (cand && typeof cand === 'object' && !Array.isArray(cand)) return cand;

  // 3) If it's a STRING, clean & parse
  if (typeof cand === 'string') {
    let s = cand.trim();

    // strip ```json fences if present
    s = s.replace(/^```(?:json)?\s*/i, '').replace(/```$/i, '');

    // isolate outermost JSON object (in case there's prose around it)
    const start = s.indexOf('{');
    const end   = s.lastIndexOf('}');
    if (start !== -1 && end !== -1) s = s.slice(start, end + 1);

    try {
      return JSON.parse(s);
    } catch (e) {
      throw new Error(
        'Could not parse model JSON. ' +
        'Type=' + typeof cand + ' Preview:\n' + s.slice(0, 400)
      );
    }
  }

  // 4) Nothing usable
  throw new Error('No usable payload: got ' + typeof cand);
}

