    .replace(/(\?|&)utm_[^=]+=[^&]*/g, "")  // drop UTM params
    .replace(/#.*$/, "");                    // drop hash

  if (!seen.has(link)) {
    seen.add(link);
    out.push({ json: { ...item.json, link } });
  }
}

return out;

