// get target job id (adjust source as needed)
const jobId = $('run_job').first().json.jobid || $flow.get('jobId');

// take the first incoming item
const first = $input.first().json;

// your HTTP node returns an array with one object; unwrap it
const root = Array.isArray(first) ? first[0] : first;

// search ONLY in finished[]
const hit = (root.finished || []).find(j => j.id === jobId);

// always return an object in `json`
if (hit) {
  return [{ json: hit }];
} else {
  return [{ json: { match: false, lookedFor: jobId } }];
}

// Input can be from HTTP Request node (string body), or a JSON wrapper with "data",
// or even binary base64. This normalizes all of those into a JL string.
function getJLString(firstItem) {
  if (!firstItem) return '';
  if (typeof firstItem.json === 'string') return firstItem.json;

  if (firstItem.json) {
    if (typeof firstItem.json.data === 'string') return firstItem.json.data;
    if (typeof firstItem.json.body === 'string') return firstItem.json.body;
  }

  if (firstItem.binary?.data) {
    return Buffer.from(firstItem.binary.data, 'base64').toString('utf8');
  }

  // Sometimes people pass the raw JL in a field named "text" or similar
  for (const k of Object.keys(firstItem.json || {})) {
    const v = firstItem.json[k];
    if (typeof v === 'string' && v.includes('{"name"') && v.includes('"url"')) {
      return v;
    }
  }
  return '';
}

function safeParse(line) {
  try { return JSON.parse(line); } catch { return null; }
}

function parsePrice(p) {
  if (typeof p === 'number') return p;
  if (typeof p === 'string') {
    const num = p.replace(/[^\d,.\-]/g, '').replace(',', '.');
    const f = parseFloat(num);
    return Number.isFinite(f) ? f : null;
  }
  return null;
}

function domainFromUrl(u) {
  try { return new URL(u).hostname; } catch { return null; }
}

function enrich(record) {
  const url = record.url || '';
  const title = record.name || '';
  const price = parsePrice(record.price);
  const domain = domainFromUrl(url);

  let path = '';
  try { path = new URL(url).pathname; } catch {}
  const parts = path.split('/').filter(Boolean); // ['en','used-part','fau19088-3834801110-hyundai-ix35-light-switch']
  const idx = parts.findIndex(s => s === 'used-part');
  let slug = [];
  if (idx >= 0 && parts[idx + 1]) slug = parts[idx + 1].split('-');

  const id = slug[0] || null;       // e.g. fau19088
  const partNo = slug[1] || null;   // e.g. 3834801110
  const make = slug[2] || null;     // e.g. hyundai
  const model = slug[3] || null;    // e.g. ix35
  const partName = slug.slice(4).join('-') || null; // e.g. light-switch

  return {
    title,
    url,
    price,
    currency: 'EUR',
    domain,
    source: domain,
    id,
    partNo,
    make,
    model,
    partName,
    ts: new Date().toISOString(),
  };
}

// ---- main ----
const jl = getJLString(items[0]);
const lines = jl.split(/\r?\n/).map(l => l.trim()).filter(Boolean);

const parsed = [];
for (const ln of lines) {
  let obj = safeParse(ln);
  if (!obj) {
    // Try to recover bare JSON when the line has prefixes/suffixes
    const m = ln.match(/\{.*\}$/);
    if (m) obj = safeParse(m[0]);
  }
  if (obj && obj.url) parsed.push(obj);
}

// Dedup by URL (keep cheapest)
const byUrl = new Map();
for (const r of parsed) {
  const e = enrich(r);
  if (!e.url) continue;
  const cur = byUrl.get(e.url);
  if (!cur || (e.price != null && (cur.price == null || e.price < cur.price))) {
    byUrl.set(e.url, e);
  }
}

// Sort by price asc (nulls last)
const out = Array.from(byUrl.values()).sort((a, b) => {
  const ap = a.price == null ? Infinity : a.price;
  const bp = b.price == null ? Infinity : b.price;
  return ap - bp;
});

// Return n8n items
return out.map(row => ({ json: row }));
