const raw = $json.output;

// Fully remove any Markdown code block wrapping
const clean = raw
  .replace(/^\s*```json\s*/i, '')  // Remove opening ```json
  .replace(/^\s*```\s*/i, '')      // Remove opening ``` if no json
  .replace(/\s*```$/, '')          // Remove trailing ```
  .trim();

let parsed;

try {
  parsed = JSON.parse(clean);
} catch (e) {
  throw new Error('Failed to parse JSON: ' + e.message + '\nContent: ' + clean);
}

return [
  {
    json: {
      urgency: parsed.urgency || 'normal',
      summary: parsed.summary || ''
    }
  }
];
