const insights = JSON.parse($json.content);
const fileName = $node['Check Report Folder'].json.name;

// Determine alert priority
let priority = 'normal';
if (insights.sentiment === 'negative' || insights.alerts?.length > 0) {
  priority = 'high';
}

// Format message
const slackBlocks = [
  {
    type: 'header',
    text: {
      type: 'plain_text',
      text: `üìÑ New Report: ${insights.reportType || fileName}`
    }
  },
  {
    type: 'section',
    fields: [
      {
        type: 'mrkdwn',
        text: `*Date:* ${insights.date || 'Not specified'}`
      },
      {
        type: 'mrkdwn',
        text: `*Priority:* ${priority}`
      }
    ]
  }
];

if (insights.metrics) {
  slackBlocks.push({
    type: 'section',
    text: {
      type: 'mrkdwn',
      text: `*Key Metrics:*\n${Object.entries(insights.metrics).map(([k, v]) => `‚Ä¢ ${k}: ${v}`).join('\n')}`
    }
  });
}

if (insights.findings) {
  slackBlocks.push({
    type: 'section',
    text: {
      type: 'mrkdwn',
      text: `*Key Findings:*\n${insights.findings.map(f => `‚Ä¢ ${f}`).join('\n')}`
    }
  });
}

return {
  fileName,
  insights,
  priority,
  slackBlocks,
  emailContent: insights
};
