const template = 'xxxxxxxx-xxxx-4xxx-yxxx-xxxxxxxxxxxx';
  let uuid = '';

  for (let i = 0; i < template.length; i++) {
    const char = template[i];

    if (char === 'x' || char === 'y') {
      const rand = Math.floor(Math.random() * 16);
      let value;

      if (char === 'x') {
        value = rand;
      } else {
        value = (rand & 0x3) | 0x8;
      }

      uuid += value.toString(16);
    } else {
      uuid += char;
    }
  }

  return {
    data: uuid
  }
