// Initialize list to hold invoice filenames
let invoiceAttachments = [];

// Check $binary for keys like 'attachment_0', 'attachment_1'
if ($binary && Object.keys($binary).length > 0) {
  for (const key of Object.keys($binary)) {
    const file = $binary[key];

    // If filename contains 'invoice', treat it as an invoice attachment
    if (
      file.fileName &&
      /invoice/i.test(file.fileName)
    ) {
      invoiceAttachments.push(file.fileName);
    }
  }
}

// Return updated JSON with invoice detection
return [
  {
    json: {
      ...$json,
      hasInvoiceAttachment: invoiceAttachments.length > 0,
      invoiceFilenames: invoiceAttachments.join(', ')
    }
  }
];

