function cleanNumber(n) {
  return parseInt(n.toString().replace(/[.,]/g, '')) || 0;
}

const items = $json.output.Items.map(item => ({
  ...item,
  Quantity: cleanNumber(item.Quantity),
  "Unit Price": cleanNumber(item["Unit Price"]),
  Total: cleanNumber(item.Total),
}));

return {
  json: {
    output: {
      ...$json.output,
      Items: items
    }
  }
};
