// Extract the JSON from the AI Agent's response
const aiResponse = $input.first().json.output;

try {
    // Find and parse the JSON part
    const jsonStart = aiResponse.indexOf('{');
    const jsonEnd = aiResponse.lastIndexOf('}');
    const jsonString = aiResponse.slice(jsonStart, jsonEnd + 1);
    const parsedResponse = JSON.parse(jsonString);

    // Return status for Switch node + original reply for later use
    return {
        status: parsedResponse.status,  // For Switch routing
        reply: parsedResponse.reply,     // Original AI-generated reply
        suggestion: parsedResponse.suggestion  // Optional (if needed)
    };
} catch (error) {
    console.error("Failed to parse AI response:", error);
    return {
        status: "invalid_response",
        reply: "Error: Could not process your request.",  // Fallback reply
        suggestion: "Check AI Agent output formatting"
    };
}
