/* ===== Base layout ===== */
html, body {
  height: 100%;
  font-family: var(--font-family);
  font-weight: var(--font-weight-normal);
  font-size: var(--font-size-body);
  background:
    radial-gradient(1200px 800px at 10% -10%, #f0f2ff 0%, transparent 60%),
    radial-gradient(1000px 700px at 110% 0%, #fef3f0 0%, transparent 55%),
    var(--color-background);
  color: var(--color-html-text);
  -webkit-font-smoothing: antialiased;
  -moz-osx-font-smoothing: grayscale;
}

.container, .form-wrapper, .n8n-form {
  max-width: var(--container-width);
  margin: 32px auto;
  padding: 0 12px;
}

/* ===== Test banner (top) ===== */
.test-notice {
  display: flex;
  align-items: center;
  gap: 8px;
  margin: 0 auto 12px;
  max-width: var(--container-width);
  background: var(--color-test-notice-bg);
  border: 1px solid var(--color-test-notice-border);
  color: var(--color-test-notice-text);
  padding: var(--padding-test-notice-vertical) var(--padding-test-notice-horizontal);
  border-radius: var(--border-radius-input);
  font-size: var(--font-size-test-notice);
}

/* ===== Card ===== */
.form-card {
  background: var(--color-card-bg);
  border: 1px solid var(--color-card-border);
  border-radius: var(--border-radius-card);
  box-shadow: var(--box-shadow-card);
  padding: var(--padding-card);
  margin-bottom: var(--margin-bottom-card);
}

/* ===== Headings & subtitle ===== */
.form-card h1,
h1.form-title {
  font-size: var(--font-size-header);
  color: var(--color-header);
  margin: 4px 0 6px;
  font-weight: var(--font-weight-bold);
  letter-spacing: -0.2px;
}

.form-subtitle {
  font-size: var(--font-size-paragraph);
  color: var(--color-header-subtext);
  margin: 0 0 16px;
  line-height: 1.5;
}

/* ===== Form fields ===== */
.form-field {
  display: grid;
  gap: 6px;
  margin-bottom: 14px;
}

.form-field label {
  font-size: var(--font-size-label);
  color: var(--color-label);
  font-weight: 600;
}

.form-field label .required {
  color: var(--color-required);
  margin-left: 2px;
}

/* Inputs / selects / textareas */
input[type="text"],
input[type="email"],
input[type="number"],
input[type="search"],
textarea,
select {
  width: 100%;
  appearance: none;
  border: 1px solid var(--color-input-border);
  border-radius: var(--border-radius-input);
  padding: 12px 14px;
  font-size: var(--font-size-input);
  color: var(--color-input-text);
  background: #fff;
  transition: border-color .15s ease, box-shadow .15s ease, transform .05s ease;
}

input::placeholder,
textarea::placeholder {
  opacity: var(--opacity-placeholder);
}

/* Hover / focus */
input:hover,
textarea:hover,
select:hover {
  border-color: #cfd5e2;
}

input:focus,
textarea:focus,
select:focus {
  outline: none;
  border-color: var(--color-focus-border);
  box-shadow: 0 0 0 4px rgba(90, 76, 194, .12);
}

/* Invalid */
.is-invalid,
input[aria-invalid="true"],
textarea[aria-invalid="true"],
select[aria-invalid="true"] {
  border-color: var(--color-error);
  box-shadow: 0 0 0 4px rgba(234, 31, 48, .12);
}

/* Help / error text */
.help-text {
  font-size: 12px;
  color: var(--color-link);
}

.error-text {
  font-size: var(--font-size-error);
  color: var(--color-error);
  margin-top: 4px;
}

/* ===== File input (custom) ===== */
.input-file {
  position: relative;
}

.input-file input[type="file"] {
  position: absolute;
  inset: 0;
  opacity: 0;
  cursor: pointer;
}

.input-file .file-ui {
  display: flex;
  align-items: center;
  justify-content: space-between;
  gap: 10px;
  border: 1px dashed var(--color-input-border);
  border-radius: var(--border-radius-input);
  padding: 12px 14px;
  color: var(--color-input-text);
  background: #fff;
  transition: border-color .15s ease, box-shadow .15s ease;
}

.input-file .file-ui:hover {
  border-color: #cfd5e2;
}

.input-file .file-ui:has(+ input[type="file"]:focus) {
  border-color: var(--color-focus-border);
  box-shadow: 0 0 0 4px rgba(90, 76, 194, .12);
}

/* ===== Checkboxes / radios (accessible but nicer) ===== */
input[type="checkbox"], input[type="radio"] {
  accent-color: var(--color-focus-border);
  width: var(--checkbox-size);
  height: var(--checkbox-size);
}

/* ===== Buttons ===== */
.actions,
.form-actions {
  margin-top: 8px;
}

button[type="submit"],
.button-submit {
  width: 100%;
  height: var(--submit-btn-height);
  background: var(--color-submit-btn-bg);
  color: var(--color-submit-btn-text);
  border: 1px solid transparent;
  border-radius: var(--border-radius-input);
  font-size: 15px;
  font-weight: 700;
  letter-spacing: .2px;
  cursor: pointer;
  transition: transform .05s ease, filter .15s ease, box-shadow .15s ease;
  box-shadow: 0 6px 18px rgba(255, 109, 90, .25);
}

button[type="submit"]:hover {
