  box-shadow: 0 8px 24px rgba(255, 109, 90, .28);
}

button[type="submit"]:active {
  transform: translateY(0);
  box-shadow: 0 6px 18px rgba(255, 109, 90, .22);
}

button[disabled] {
  opacity: .6;
  cursor: not-allowed;
}

/* Secondary link under button (e.g., “Clear form”) */
