const results = $('Google_news search').first().json.news_results || [];
console.log("results: " + results.length);
const out = [];

const batchSize = 10;

const url = "http://localhost:5678/webhook-test/news?p=";

let indNews = Number($input.first().json.Counter);
console.log("indNews start: " + indNews);
console.log("indNews after: " + Number(indNews + batchSize));

let ind = 0;
for (const res of results) {

  if (ind >= indNews && ind < (indNews + batchSize)) {
    const src = res.source || {};
    if (src && Object.keys(src).length === 0 && src.constructor === Object) {
      const stories = res.stories || {};
      const src1 = stories[0].source || {};
      out.push({
        json: {
          position: Number(res.position),
          title: res.title,
          source: src1.name || null,
          authors: (src1.authors || []).join(", "), 
          link2: url + res.position,
          link1: src1.link,
        }
      }); 
    } else {
      out.push({
        json: {
          position: Number(res.position),
          title: res.title,
          source: src.name || null,
          authors: (src.authors || []).join(", "), 
          link2: url + Number(res.position),
          link1: res.link,
        }
      });     
    }    
  } 
  ind++;
  //else {
  //  break;
  //}
}

console.log("ind end: " + Number(indNews + batchSize));

// optional: sort by parent position then story pos
out.sort((a, b) =>
  (a.json.position ?? Infinity) - (b.json.position ?? Infinity)
);
return { out: out, counter: Number(indNews + batchSize) };
