const base = 'https://www.linkedin.com/search/results/content/';
const qp = [
  `datePosted=${encodeURIComponent('"past-24h"')}`,
  `keywords=${encodeURIComponent($input.first().json.Keywords)}`,
  `origin=${encodeURIComponent('FACETED_SEARCH')}`
].join('&');

return [{ json: { url: `${base}?${qp}` } }];
