// Get configuration from the Set node
const config = $('Set: Config').item.json;
const rejectStatuses = config.REJECT_STATUS_CSV.split(',').map(s => s.trim());

// Filter candidates that need rejection emails
const candidatesToEmail = [];
const skippedCandidates = [];

for (const item of $input.all()) {
  const data = item.json;

  // Skip if no candidate email
  if (!data.candidate_email || data.candidate_email.trim() === '') {
    skippedCandidates.push({ reason: 'No email', data });
    continue;
  }

  // Skip if rejection already sent
  if (data.rejection_sent_at && data.rejection_sent_at.trim() !== '') {
    skippedCandidates.push({ reason: 'Already sent', data });
    continue;
  }

  // Check if status matches rejection criteria (case-sensitive)
  if (!rejectStatuses.includes(data.status)) {
    skippedCandidates.push({ reason: 'Status not in reject list', data });
    continue;
  }

  // Skip if missing required fields
  if (!data.candidate_name || !data.role || !data.company_name || !data.recruiter_name) {
    skippedCandidates.push({ reason: 'Missing required fields', data });
    continue;
  }

  candidatesToEmail.push(data);
}

console.log(`Found ${candidatesToEmail.length} candidates to email`);
console.log(`Skipped ${skippedCandidates.length} candidates`);

// Return the filtered candidates
return candidatesToEmail.map(candidate => ({ json: candidate }));

// Dry run preview
const config = $('Set: Config').item.json;
const candidates = $input.all();

console.log('DRY RUN MODE - Preview of emails to be sent:');
console.log('='.repeat(50));

for (let i = 0; i < candidates.length; i++) {
  const candidate = candidates[i].json;

  // Process subject template
  let subject = config.SUBJECT_TEMPLATE
    .replace(/{{candidate_name}}/g, candidate.candidate_name || '')
    .replace(/{{role}}/g, candidate.role || '')
    .replace(/{{company_name}}/g, candidate.company_name || '')
    .replace(/{{recruiter_name}}/g, candidate.recruiter_name || '');

  console.log(`${i + 1}. To: ${candidate.candidate_email}`);
  console.log(`   Subject: ${subject}`);
  console.log(`   Status: ${candidate.status}`);
  console.log(`   Role: ${candidate.role}`);
  console.log('');
}

console.log(`Total emails that would be sent: ${candidates.length}`);
console.log('='.repeat(50));

return [{ json: { 
  message: `DRY RUN: Would send ${candidates.length} rejection emails`, 
  candidates_count: candidates.length,
  dry_run: true
}}];

// Process email template for current candidate
const config = $('Set: Config').item.json;
const candidate = $json;

// Process feedback if available
let feedbackHtml = '';
let feedbackText = '';
if (candidate.interview_feedback && candidate.interview_feedback.trim() !== '') {
  feedbackHtml = `<p><strong>Feedback:</strong> ${candidate.interview_feedback}</p>`;
  feedbackText = `\nFeedback: ${candidate.interview_feedback}\n`;
}

// Process HTML template
let htmlBody = config.HTML_TEMPLATE
  .replace(/{{candidate_name}}/g, candidate.candidate_name || '')
  .replace(/{{role}}/g, candidate.role || '')
  .replace(/{{company_name}}/g, candidate.company_name || '')
  .replace(/{{recruiter_name}}/g, candidate.recruiter_name || '')
  .replace(/{{feedback_html}}/g, feedbackHtml);

// Process text template
let textBody = config.TEXT_TEMPLATE
  .replace(/{{candidate_name}}/g, candidate.candidate_name || '')
  .replace(/{{role}}/g, candidate.role || '')
  .replace(/{{company_name}}/g, candidate.company_name || '')
  .replace(/{{recruiter_name}}/g, candidate.recruiter_name || '')
  .replace(/{{feedback_text}}/g, feedbackText);

// Process subject template
let subject = config.SUBJECT_TEMPLATE
  .replace(/{{candidate_name}}/g, candidate.candidate_name || '')
  .replace(/{{role}}/g, candidate.role || '')
  .replace(/{{company_name}}/g, candidate.company_name || '')
  .replace(/{{recruiter_name}}/g, candidate.recruiter_name || '');

return {
  json: {
    ...candidate,
    email_subject: subject,
    email_html: htmlBody,
    email_text: textBody,
    smtp_from: config.SMTP_FROM
  }
};
