// Parse NVD list → newest top N CVEs with Vendor/Product/Version, severity/CVSS,
// exploit signal, and brief English summary.

const LIMIT = 20; // how many to keep

const vulns = $json.vulnerabilities ?? [];

/* ---------------- helpers ---------------- */

function getCvssAndSeverity(cve) {
  const m31 = cve?.metrics?.cvssMetricV31?.[0];
  const m30 = cve?.metrics?.cvssMetricV30?.[0];
  const m2  = cve?.metrics?.cvssMetricV2 ?. [0];

  const cvss =
    m31?.cvssData?.baseScore ??
    m30?.cvssData?.baseScore ??
    m2 ?.cvssData?.baseScore ?? null;

  const severity =
    m31?.cvssData?.baseSeverity ??
    m30?.cvssData?.baseSeverity ??
    (cvss != null
      ? (cvss >= 9 ? 'CRITICAL' : cvss >= 7 ? 'HIGH' : cvss >= 4 ? 'MEDIUM' : 'LOW')
      : 'UNKNOWN');

  const vector =
    m31?.cvssData?.vectorString ??
    m30?.cvssData?.vectorString ??
    m2 ?.cvssData?.vectorString ?? null;

  return { cvss, severity, vector };
}

// Pull vendor/product/version from CPE 2.3 (when available)
function getCpeTriples(cve) {
  const triples = [];
  const seen = new Set();

  const walk = (node) => {
    const matches = node?.cpeMatch || node?.cpeMatches || [];
    for (const m of matches) {
      const cpe = m.criteria || m.cpe23Uri || m.cpeName || '';
      // cpe:2.3:<part>:<vendor>:<product>:<version>:...
      const parts = cpe.split(':');
      if (parts.length >= 6) {
        const vendor  = (parts[3] || '').toLowerCase();
        const product = (parts[4] || '').toLowerCase();
        const version = (parts[5] && parts[5] !== '*' && parts[5] !== '-') ? parts[5] : null;
        const key = `${vendor}|${product}|${version||''}`;
        if (vendor && product && !seen.has(key)) {
          seen.add(key);
          triples.push({ vendor, product, version });
        }
      }
    }
    (node?.children || node?.nodes || []).forEach(walk);
  };

  (cve?.configurations?.nodes || []).forEach(walk);
  return triples;
}

// Heuristic vendor/product from English summary (no version required)
function vendorProductFromText(desc) {
  const text = String(desc || '');

  // Known vendors (extend as needed)
  const vendorCatalog = [
    'ibm','microsoft','cisco','oracle','red hat','apache','nginx','wordpress',
    'kapsch trafficcom','hpe','hp','dell','lenovo','sap','vmware','fortinet',
    'palo alto','juniper','citrix','f5','adobe','atlassian','gitlab','github',
    'grafana','jetbrains','sonicwall','progress','solarwinds','check point'
  ];

  let vendor = null, product = null;

  for (const v of vendorCatalog) {
    const re = new RegExp(`\\b${v.replace(/\s+/g,'\\s+')}\\b`, 'i');
    if (re.test(text)) { vendor = v; break; }
  }

  // WordPress plugin: "<Plugin Name> plugin for WordPress"
  const mWp = text.match(/([\w()[\] .&\-]{3,80}?)\s+plugin\s+for\s+WordPress/i);
  if (mWp) {
    product = mWp[1].trim();
    vendor = vendor || 'wordpress';
  }

  // If we know the vendor, capture product phrase right after it
  if (vendor && !product) {
    const vRe = new RegExp(
      `\\b${vendor.replace(/\s+/g,'\\s+')}\\b\\s+` +
      `([A-Z][\\w()&\\-]+(?:\\s[A-Z][\\w()&\\-]+){0,5})\\s+` +
      `(?:is|are|was|were|contains|includes|allows|vulnerable|prior|before|up|due)`,
      'i'
    );
    const m = text.match(vRe);
    if (m) product = m[1].trim();
  }

  // Generic fallback: first capitalized phrase before a verb
  if (!product) {
    const m2 = text.match(
      /([A-Z][\w()&\-]+(?:\s[A-Z][\w()&\-]+){1,5})\s+(?:is|are|was|were|contains|includes|allows|vulnerable)/);
    if (m2) product = m2[1].trim();
  }

  return {
    vendor: (vendor || 'unknown').toLowerCase(),
    product: (product || 'unknown').toLowerCase(),
  };
}

// Collect version strings from description (e.g., "v3.2.0", "version 4.6.0")
function collectTextVersions(desc) {
  const text = String(desc || '');
  const out = new Set();
  for (const m of text.matchAll(/\b(?:v(?:ersion)?\s*)(\d[\dA-Za-z.\-_]+)\b/gi)) {
    out.add(m[1]);
  }
  return [...out];
}

// Detect public exploit presence from references
function exploitInfo(cve) {
  const refs = cve.references ?? [];
  const urls = [];
  for (const r of refs) {
    const tags = (r.tags || []).map(t => String(t).toLowerCase());
    const url = r.url || '';
    const tagHit = tags.includes('exploit') || tags.includes('proof of concept');
    const urlHit = /exploitdb|packetstorm|metasploit|github\.com\/.*(poc|exploit)/i.test(url);
    if (tagHit || urlHit) urls.push(url);
  }
  return { hasExploit: urls.length > 0, exploitUrls: urls.slice(0, 3) };
}

/* ---------------- main ---------------- */

// newest first (published → lastModified)
vulns.sort((a,b) =>
  Date.parse(b.cve?.published ?? b.cve?.lastModified ?? 0) -
  Date.parse(a.cve?.published ?? a.cve?.lastModified ?? 0)
);

// take top N
const selected = vulns.slice(0, LIMIT);

// map to output items
return selected.map(v => {
  const c = v.cve;
  const { cvss, severity, vector } = getCvssAndSeverity(c);

  const desc = (c.descriptions ?? []).find(d => d.lang === 'en')?.value
            ?? (c.descriptions?.[0]?.value ?? '');

  const triples  = getCpeTriples(c);
  const fromText = vendorProductFromText(desc);

  const vendor  = (triples[0]?.vendor)  || fromText.vendor  || 'unknown';
  const product = (triples[0]?.product) || fromText.product || 'unknown';

  // versions from CPE + description
  const versions = new Set();
  triples.forEach(t => { if (t.version) versions.add(t.version); });
  collectTextVersions(desc).forEach(vv => versions.add(vv));

  const { hasExploit, exploitUrls } = exploitInfo(c);
  const id   = c.id;
  const link = `https://nvd.nist.gov/vuln/detail/${id}`;

  return {
    json: {
      cveId: id,
      link,
      vendor,
      product,
      versions: Array.from(versions).slice(0, 5), // keep tidy
      severity,
      cvss,
      cvssVector: vector,
      summary: desc.slice(0, 240),
      hasExploit,
      exploitUrls,
      published: c.published ?? null
    }
  };
});

if (!items.length) {
  return [{ json: { send:false, subject:"CVE Digest: 0 items", text:"No CVEs.", html:"<p>No CVEs.</p>" } }];
}

function esc(s){return String(s??'').replace(/[&<>"']/g,m=>({ '&':'&amp;','<':'&lt;','>':'&gt;','"':'&quot;',"'":'&#39;' }[m]));}

const ord = { CRITICAL:0, HIGH:1, MEDIUM:2, LOW:3, UNKNOWN:4 };
const list = items.map(i=>i.json).sort((a,b)=>{
  const sa = ord[(a.severity||'UNKNOWN').toUpperCase()] ?? 9;
  const sb = ord[(b.severity||'UNKNOWN').toUpperCase()] ?? 9;
  return sa - sb || (b.cvss??-1) - (a.cvss??-1);
});

const counts = list.reduce((acc,j)=>{
  const s=(j.severity||'UNKNOWN').toUpperCase();
  acc[s]=(acc[s]||0)+1; return acc;
}, {});

const subject = `Top ${list.length} Latest CVEs — CRIT:${counts.CRITICAL||0} HIGH:${counts.HIGH||0}`;

const rows = list.map(j=>{
  const link = j.link ? `<a href="${esc(j.link)}">${esc(j.cveId)}</a>` : esc(j.cveId);
  const versions = (j.versions||[]).join(', ') || '—';
  const exploit = j.hasExploit ? 'Yes' : 'No';
  return `
    <tr>
      <td>${link}</td>
      <td>${esc(j.vendor||'unknown')}</td>
      <td>${esc(j.product||'unknown')}</td>
      <td>${versions}</td>
      <td>${esc(j.severity||'UNKNOWN')}</td>
      <td>${j.cvss!=null?esc(j.cvss):'n/a'}</td>
      <td>${exploit}</td>
      <td>${esc(j.summary||'')}</td>
    </tr>`;
}).join('');

const html = `
  <div style="font-family:system-ui,Segoe UI,Roboto,Arial,sans-serif">
    <h3 style="margin:0 0 8px 0;">${esc(subject)}</h3>
    <table border="1" cellpadding="6" cellspacing="0" style="border-collapse:collapse;font-size:14px;min-width:900px">
      <thead>
        <tr style="background:#f3f4f6;">
          <th align="left">CVE</th>
          <th align="left">Vendor</th>
          <th align="left">Product</th>
          <th align="left">Version(s)</th>
          <th align="left">Severity</th>
          <th align="left">CVSS</th>
          <th align="left">Exploit?</th>
          <th align="left">Brief description</th>
        </tr>
      </thead>
      <tbody>${rows}</tbody>
    </table>
  </div>
`;

// plain text fallback
const textLines = list.map(j=>{
  const versions = (j.versions||[]).join(', ') || '—';
  const sc = j.cvss!=null?j.cvss:'n/a';
  const ex = j.hasExploit?'Yes':'No';
  return `• ${j.cveId} | ${j.vendor||'unknown'} | ${j.product||'unknown'} | versions: ${versions} | ${j.severity||'UNKNOWN'} | CVSS ${sc} | Exploit: ${ex}\n  ${j.link}\n  ${j.summary||''}`;
});
const text = [subject, '', ...textLines].join('\n');

return [{ json: { send:true, subject, html, text } }];
