try {
  // 1. Configurable windows
  const SMA_SHORT = 60;
  const SMA_LONG  = 120;
  const NEEDED    = SMA_LONG + 1;  // long window + one extra day
  // 2. Pull in all rows (each item.json has { id, symbol, Date, Close })
  const rows = items.map(i => i.json);
  // 3. Group rows by symbol
  const bySymbol = {};
  for (const r of rows) {
    if (!bySymbol[r.symbol]) bySymbol[r.symbol] = [];
    bySymbol[r.symbol].push(r);
  }
  // 4. SMA helper
  function sma(arr, n, offset = 0) {
    const slice = arr.slice(offset, offset + n);
    return slice.reduce((sum, v) => sum + v, 0) / n;
  }
  // 5. Compute per‚Äêsymbol
  const output = [];
  for (const symbol of Object.keys(bySymbol)) {
    const group = bySymbol[symbol]
      .sort((a, b) => new Date(b.Date) - new Date(a.Date))  // newest‚Üíoldest
      .slice(0, NEEDED);
    if (group.length < NEEDED) {
      output.push({
        json: {
          symbol,
          error: `Not enough data for ${symbol}: ${group.length}/${NEEDED} days`
        }
      });
      continue;
    }
    const closes = group.map(r => parseFloat(r.Close));
    output.push({
      json: {
        symbol,
        sma60_current:   sma(closes, SMA_SHORT, 0),
        sma120_current:  sma(closes, SMA_LONG,  0),
        sma60_previous:  sma(closes, SMA_SHORT, 1),
        sma120_previous: sma(closes, SMA_LONG,  1),
      }
    });
  }
  return output;
} catch (err) {
  // global error fallback
  return [{ json: { error: err.message } }];
}

return items.map(item => {
  const payload = item.json;
  const ts      = payload["Time Series (Daily)"];

  // ‚Üê GUARD against missing or bad TS object
  if (!ts || typeof ts !== 'object') {
    return {
      json: {
        symbol: payload["Meta Data"]?.["2. Symbol"] || null,
        error:  payload.Note 
             || payload["Error Message"] 
             || "No time series returned",
      }
    };
  }

  const dates = Object.keys(ts).sort((a,b) => new Date(a) - new Date(b));
  if (dates.length < 2) {
    throw new Error("Not enough data to pick the day before last");
  }

  const date = dates[dates.length - 2];
  const day  = ts[date];

  return {
    json: {
      symbol: payload["Meta Data"]["2. Symbol"],
      date,
      close: day["4. close"],
    }
  };
});
