const raw = $json.data || $json || "";

// Case 1: If it's already parsed JSON with data[0].url
if (Array.isArray($json.data)) {
  return {
    json: {
      audioUrl: $json.data[0]?.url || null,
      rawJson: $json.data
    }
  };
}

// Case 2: Handle raw SSE text
const match = typeof raw === "string" ? raw.match(/data:\s*(\[[^\]]+\])/s) : null;

if (match) {
  try {
    const parsed = JSON.parse(match[1]);
    return {
      json: {
        audioUrl: parsed?.[0]?.url || null,
        rawJson: parsed
      }
    };
  } catch (e) {
    return {
      json: {
        error: "Failed to parse SSE JSON",
        raw
      }
    };
  }
}

// Case 3: If neither worked
return {
  json: {
    error: "No audio URL found",
    raw
  }
};
