// Normalize Msg Waiting result so the IF node always has something real to check

// 1) Always get sessionId from the earlier node (not from this node's $json)
const sessionId = $('Edit Fields').item.json.sessionId;

// 2) Detect "no rows": either zero items OR one empty item
const noRows =
  items.length === 0 ||
  (items.length === 1 && (!items[0].json || Object.keys(items[0].json).length === 0));

if (noRows) {
  // First-ever message for this user → pretend we have a row that says "not waiting yet"
  return [{ json: { user_id: sessionId, is_waiting: false, last_updated: null } }];
}

// 3) Rows exist → pass them through (and ensure required keys exist)
return items.map(it => ({
  json: {
    user_id: it.json.user_id ?? sessionId,
    is_waiting: it.json.is_waiting ?? false,
    last_updated: it.json.last_updated ?? null,
  },
}));

// n8n Code node

const rowNumbers = items.map(item => item.json.row_number);
const sessionId = items[0].json.user_id; // Assuming all rows are from the same session/user

const merged = items
  .map(item => item.json.message)
  .filter(msg => msg && msg.trim() !== '')
  .join(' '); // Or use '\n' if line breaks are preferred

return [
  {
    json: {
      message: merged,
      start_row_number: Math.min(...rowNumbers),
      row_count: items.length,
      session_id: sessionId,
    },
  },
];
