// Extract the file key from the upload response
const uploadResponse = $input.first().json;
const fileKey = uploadResponse.Key;

// Construct the public URL
const publicUrl = `https://raeojxaquatmtzgxqqdx.supabase.co/storage/v1/object/public/${fileKey}`;

return {
  json: {
    ...uploadResponse,
    publicUrl: publicUrl,
    // Keep original message data for the AI Agent
    originalMessage: $('Telegram Trigger').first().json.message
  }
};

// n8n Function node: aggregate images and create Nano Banana API payload
const items = $input.all();
const images = [];
let baseData = {};

// Loop through all incoming items
for (let i = 0; i < items.length; i++) {
  const item = items[i];

  // Copy shared metadata from first item
  // Handle the new AI node output structure
  if (i === 0) {
    // The data structure from Split Out maintains the original structure
    const content = item.json.message?.content || item.json; // Fallback for old structure
    baseData = {
      image_name: content.image_name,
      gemini_prompt: content.gemini_prompt,
      image_urls: content.image_urls,
      original_output: content.original_output || 'Generated by AI node',
    };
  }

  // Extract binary data and convert to inline_data format
  if (item.binary && item.binary.data) {
    images.push({
      inline_data: {
        mime_type: item.binary.data.mimeType || 'image/jpeg',
        data: item.binary.data.data // Already base64 in n8n
      }
    });
  }
}

// Create the complete Nano Banana API payload
const nanoBananaPayload = {
  contents: [{
    parts: [
      {
        text: baseData.gemini_prompt
      },
      ...images
    ]
  }],
  generationConfig: {
    maxOutputTokens: 1290
  }
};

// Return single aggregated item with API-ready payload
return [{
  json: {
    ...baseData,
    nano_banana_payload: nanoBananaPayload,
    // Keep individual images for debugging if needed
    images_debug: images,
    images_count: images.length
  }
}];

// Extract base64 image from Nano Banana response - UPDATED for text+image response
const response = $input.first().json;

try {
  // Find the part that contains inlineData (image)
  const parts = response.body.candidates[0].content.parts;
  const imagePart = parts.find(part => part.inlineData);

  if (!imagePart) {
    throw new Error("No image found in response");
  }

  const imageData = imagePart.inlineData.data;
  const mimeType = imagePart.inlineData.mimeType;
  const imageName = $input.first().json.image_name || 'generated_image';

  // Also extract the text description if you want it
  const textPart = parts.find(part => part.text);
  const description = textPart ? textPart.text : '';

  console.log("Successfully extracted image data, size:", imageData.length);

  return [{
    json: {
      image_name: imageName,
      mime_type: mimeType,
      description: description, // Nano Banana's description of what it created
      success: true
    },
    binary: {
      data: {
        data: imageData,
        mimeType: mimeType,
        fileName: `${imageName}.png`
      }
    }
  }];
} catch (error) {
  console.log("Error extracting image:", error.message);
  return [{
    json: {
      error: error.message,
      response_structure: response.body
    }
  }];
}
