// Input: item from "Normalize LLM Output"
const { ticket, label, threshold, decision, llm_error } = $('Normalize LLM Output').first().json;

const lbl = label || 'triaged:ai';

// --- Current values ---
const origPri  = ticket.priority || 'Unknown';
const origComp = (Array.isArray(ticket.components) && ticket.components.length) ? ticket.components[0] : 'None';
const labelExists = Array.isArray(ticket.labels) && ticket.labels.includes(lbl);

// --- Proposed targets ---
const targetPri  = decision?.priority?.target;
const targetComp = decision?.component?.target;

// --- Changes ---
let wantPri = decision.priority?.action === 'update'
  && Number(decision.priority.confidence) >= Number(threshold)
  && targetPri && targetPri !== origPri;

let wantComp = decision.component?.action === 'update'
  && Number(decision.component.confidence) >= Number(threshold)
  && targetComp && targetComp !== 'Other'
  && targetComp !== origComp;

if (llm_error) { wantPri = false; wantComp = false; }

const willAddLabel = (!labelExists) && !llm_error;

// --- Build Jira Update payload ---
const fields = {};
if (wantPri)  fields.priority   = { name: targetPri };
if (wantComp) fields.components = [{ name: targetComp }];

const jiraUpdateBody = {};
if (Object.keys(fields).length) jiraUpdateBody.fields = fields;
if (willAddLabel) jiraUpdateBody.update = { labels: [{ add: lbl }] };

// --- Build comment body ---
const lines = [];

// ✅ 1-line summary
let summary = [];
summary.push(wantPri ? `Priority → ${targetPri}` : `Priority kept (${origPri})`);
summary.push(wantComp ? `Component → ${targetComp}` : `Component kept (${origComp})`);
if (willAddLabel) summary.push(`Label added`);
lines.push(`*Triage Summary:* ${summary.join(" | ")}`);
lines.push("");

// Suggested Next Steps + Missing Info
if ((decision.guidance && decision.guidance.length) || (decision.missing_info && decision.missing_info.length)) {
  lines.push("*Suggested Next Steps*");
  if (Array.isArray(decision.guidance)) decision.guidance.forEach(g => lines.push(`- ${g}`));
  if (Array.isArray(decision.missing_info) && decision.missing_info.length) {
    lines.push("");
    lines.push("*⚠️ Missing Information*");
    decision.missing_info.forEach(m => lines.push(`- ${m}`));
  }
  lines.push("");
  lines.push("----");
  lines.push("");
}

// Audit
lines.push("*Audit of Triage*");
if (llm_error) {
  lines.push(`⚠️ LLM error (no field updates; label suppressed): ${llm_error}`);
  lines.push("");
}
lines.push(`*Issue:* ${ticket.key} (id: ${ticket.id})`);
lines.push("");

// Priority decision
lines.push("*Priority decision:*");
if (wantPri) {
  lines.push(`- Action: update [${origPri} → ${targetPri}]`);
} else {
  lines.push(`- Action: keep [${origPri}]`);
}
if (decision.priority?.justification) lines.push(`- Justification: \"${decision.priority.justification}\"`);
if (decision.priority?.confidence != null) lines.push(`- Confidence: ${Number(decision.priority.confidence).toFixed(2)}`);
lines.push(`- Verdict: ${wantPri ? "✅ updated" : "ℹ️ kept"}`);
lines.push("");

// Component decision
lines.push("*Component decision:*");
if (wantComp) {
  lines.push(`- Action: update [${origComp} → ${targetComp}]`);
} else {
  lines.push(`- Action: keep [${origComp}]`);
}
if (decision.component?.justification) lines.push(`- Justification: \"${decision.component.justification}\"`);
if (decision.component?.confidence != null) lines.push(`- Confidence: ${Number(decision.component.confidence).toFixed(2)}`);
lines.push(`- Verdict: ${wantComp ? "✅ updated" : "ℹ️ kept"}`);
lines.push("");

// Label decision
lines.push("*Label decision:*");
if (llm_error) {
  lines.push(`- ${lbl}: [absent → not added due to LLM error]`);
} else if (willAddLabel) {
  lines.push(`- ${lbl}: [absent → added]`);
} else {
  lines.push(`- ${lbl}: [present → kept]`);
}

return [{
  json: {
    issueKey: ticket.key,
    issueId: ticket.id,
    jiraUpdateBody,
    decision,
    jsmCommentBody: { public: false, body: lines.join("\n") }
  }
}];

// Support both shapes:
// - Webhook: { body: { issue: { key, id, fields: {...} } }, ... }
// - Direct GET: { key, id, fields: {...}, ... }
const srcIssue = $json.body?.issue ?? $json;
const fields   = srcIssue?.fields ?? {};

function safeStr(v) { return (v ?? "").toString(); }

// Components may be objects or strings
const components = Array.isArray(fields.components)
  ? fields.components.map(c => (typeof c === 'string' ? c : c?.name)).filter(Boolean)
  : [];

// Labels from Jira fields
const labels = Array.isArray(fields.labels) ? fields.labels : [];

// Priority can be object or string
const priorityName = fields.priority?.name ?? (typeof fields.priority === 'string' ? fields.priority : 'Unknown');

// Parse domain (it may arrive as a JSON string)
let domainObj = {};
try {
  domainObj = typeof $json.domain === 'string' ? JSON.parse($json.domain) : ($json.domain || {});
} catch {
  domainObj = {};
}

// Confidence threshold
const conf = Number($json.CONF_THRESH);
const confidence_threshold = Number.isFinite(conf) ? conf : 0.7;

return [{
  json: {
    ticket: {
      key: safeStr(srcIssue?.key),
      id: safeStr(srcIssue?.id),
      summary: safeStr(fields.summary),
      description: safeStr(fields.description),
      priority: safeStr(priorityName),
      components,
      labels,
    },
    domain: domainObj,
    constraints: {
      confidence_threshold,
    },
  }
}];

// Get the original comment skeleton
const base = $items("Build JIRA Update", 0, $itemIndex)?.[0]?.json || {};

// The current item is the HTTP response (because Full Response = true)
const status = $json?.statusCode;
const text = typeof $json?.body === 'string' ? $json.body : JSON.stringify($json?.body || {});

let body = base.jsmCommentBody?.body || "(no audit body)";
const failed = typeof status === 'number' && (status < 200 || status >= 300);

if (failed) {
  body = `⚠️ Jira update failed\nStatus: ${status}\nBody: ${text}\n\n` + body;
}

// Output the final comment payload
return [{
  json: {
    issueKey: base.issueKey,
    jsmCommentBody: { public: false, body }
  }
}];

// Source A: comment/ctx
const ctx = $json;

// Source B: Build JIRA Update (decision, confidence, targets)
const updateCtx = $items("Build JIRA Update", 0, $itemIndex)[0]?.json || {};

// Source C: JIRA Update (API response with statusCode)
const apiCtx = $items("JIRA Update", 0, $itemIndex)[0]?.json || {};

return [{
  json: {
    issueKey: ctx.issueKey || updateCtx.issueKey,
    issueId: ctx.issueId || updateCtx.issueId,

    llmError: !!(ctx.llm_error || updateCtx.llm_error),

    // Changes applied (from the update payload)
    priorityChange: !!updateCtx.jiraUpdateBody?.fields?.priority,
    componentChange: !!updateCtx.jiraUpdateBody?.fields?.components,
    labelAdded: !!updateCtx.jiraUpdateBody?.update?.labels,

    // API statuses (prefer JIRA Update response)
    jiraUpdateStatus: apiCtx.statusCode ?? null,
    jiraCommentStatus: ctx.id ? 201 : null,

    // Confidence values (from LLM decision)
    priorityConfidence: updateCtx.decision?.priority?.confidence ?? null,
    componentConfidence: updateCtx.decision?.component?.confidence ?? null,
  }
}];

// Pull the original structured payload sent to the LLM
const base  = $items("Build Payload for LLM", 0, $itemIndex)[0]?.json || {};
const setup = $items("SET_SETUP", 0, 0)[0]?.json || {};

const threshold = Number(base.constraints?.confidence_threshold ?? setup.CONF_THRESH ?? 0.7);

function readRawLLMText() {
  return (
    $json?.content?.parts?.[0]?.text ??
    $json?.candidates?.[0]?.content?.parts?.[0]?.text ??
    ""
  );
}

let llm_error = null;
let parsed = null;

const rawText = readRawLLMText();

// Try to extract a JSON object (with or without code fences)
try {
  const match =
    rawText.match(/```json\s*([\s\S]*?)\s*```/i) ||
    rawText.match(/{[\s\S]*}/);

  if (!match) throw new Error("No JSON-like content in LLM output");

  parsed = JSON.parse(match[1] || match[0]);
} catch (e) {
  llm_error = `LLM output was not valid JSON: ${e.message}`;
}

// Current values from the ticket
const currentPri  = base.ticket?.priority ?? 'Medium';
const currentComp = base.ticket?.components?.[0] ?? 'Other';

// Normalize + coerce action when target implies a change
function coerceAction(suggestedAction, target, current) {
  const s = String(suggestedAction || 'keep').toLowerCase();
  if (target && target !== current) return 'update';
  return (s === 'update') ? 'update' : 'keep';
}

// Normalize (fallbacks if LLM failed)
const pri  = parsed?.priority  ?? {};
const comp = parsed?.component ?? {};

const priorityAction  = coerceAction(pri.action,  pri.target,  currentPri);
const componentAction = coerceAction(comp.action, comp.target, currentComp);

return [{
  json: {
    ticket: base.ticket || {},
    domain: base.domain,
    threshold,
    label: setup.JIRA_AI_LABEL || 'triaged:ai',
    llm_error, // carry forward
    decision: {
      priority: {
        action: priorityAction,
        target: (pri.target ?? currentPri),
        justification: (pri.justification ?? ''),
        confidence: Number(pri.confidence ?? 0),
      },
      component: {
        action: componentAction,
        target: (comp.target ?? currentComp),
        justification: (comp.justification ?? ''),
        confidence: Number(comp.confidence ?? 0),
      },
      missing_info: Array.isArray(parsed?.missing_info) ? parsed.missing_info : [],
      guidance: Array.isArray(parsed?.guidance) ? parsed.guidance : [],
    }
  }
}];
